INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('5', '2', '公司简介', '<p style=\"text-align: left;\">&nbsp;</p>\r\n<p style=\"text-align: left;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: small;\"><b><span style=\"font-family: 宋体;\">原味生活简介：</span></b></span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 10.5pt; line-height: 15pt; text-align: left;\"><span style=\"font-size: small;\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51);\">镇江市原味生活农业科技有限公司，简称<span lang=\"EN-US\">&quot;</span>原味生活<span lang=\"EN-US\">&quot;(www.yw0511.com)</span>是一个以短半径、可追溯、受监督、安全、好吃、高质为理念，提供&ldquo;连婴儿和妈妈都可以吃得放心&rdquo;的<span lang=\"EN-US\">B2C</span>安全食品幸福超市。</span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 10.5pt; line-height: 15pt; text-align: left;\"><span style=\"font-size: small;\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51);\">原味生活目前主要以线上销售签约农户的<span lang=\"EN-US\">0</span>农药有机肥蔬菜套餐为特色，未来将辅以家庭日常所需的五谷杂粮、新鲜禽肉蛋类等食品，通过镇江市这种短半径内的供给、实名制的可追溯体制、受消费者和原味生活的共同监督、定期不定期检测的安全体制、再现原味的好吃和高质量，为消费者提供可信赖的安全高质的食品。</span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 10.5pt; line-height: 15pt; text-align: left;\"><span style=\"mso-bidi-font-size:\r\n10.5pt;font-family:宋体;mso-bidi-font-family:Arial;color:#333333;mso-font-kerning:\r\n0pt\"><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p style=\"text-align: left;\">&nbsp;</p>\r\n<p style=\"text-align: left;\"><span style=\"color: rgb(255, 102, 0);\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"font-size: small;\"><b><span style=\"font-family: 宋体;\">原味生活愿景</span></b></span></span></span></p>\r\n<p style=\"text-align: left;\"><b><span style=\"font-family: 宋体;\"><span lang=\"EN-US\"><o:p></o:p></span></span></b><span style=\"font-size: small;\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51);\">&nbsp; 尝试解决一部分对食品安全有着强烈诉求的特殊人群（母婴、化学物质过敏性体质人群、疾病患者、健康生活提倡者等）的饮食问题，诚实、负责的为原味生活每个会员的</span></span><span style=\"font-size: small;\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51);\">家庭服务好每一天。</span></span><span style=\"mso-bidi-font-size:10.5pt;font-family:宋体;mso-bidi-font-family:\r\nArial;color:#333333;mso-font-kerning:0pt\"><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p style=\"text-align: left;\"><span style=\"font-size: small;\"><span lang=\"EN-US\">&nbsp;</span></span></p>', '', '', '公司简介', '0', '1', '1340781228', '', '0', 'http://', '公司简介');
