INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('45', '2', '原味生活的食品安全监督沙龙', '<p>&nbsp;</p>\r\n<p><span style=\"color: rgb(51, 51, 51);\"><span style=\"font-size: small;\"><span style=\"font-family: 宋体; font-weight: bold;\">原味生活的食品安全监督沙龙是做什么的？</span><span style=\"font-family: Osaka;\">&nbsp;</span><span style=\"font-family: Osaka;\"><br />\r\n<br />\r\n</span><span style=\"font-family: Osaka;\">　</span><span style=\"font-family: 宋体;\">在这个食品安全问题勃发的年代，人们经常抱头问自己&ldquo;我还能吃什么？&rdquo;。导致这样一个局面的因素是多方面的，农业上的食品安全问题</span><span style=\"font-family: 宋体;\">也</span><span style=\"font-family: 宋体;\">不可能从一个环节上得以制止。在这样的一个背景下，原味生活</span><span style=\"font-family: 宋体;\">尝试在一个固定的供应链条内，让消费者和生产者进行更为多元的接触，尤其是消费者为自己家庭一日三餐的出处进行定期不定期的监督检查，来尝试解决这一问题，因此设置了食品安全监督沙龙。</span><span style=\"font-family: Osaka;\"><br />\r\n<br />\r\n</span><span style=\"font-family: 宋体; font-weight: bold;\">以消费者为代表的</span><span style=\"font-family: 宋体; font-weight: bold;\">第三方组织监督生产</span><span style=\"font-family: 宋体; font-weight: bold;\">和</span><span style=\"font-family: 宋体; font-weight: bold;\">供应</span><span style=\"font-family: Osaka;\"><br />\r\n<br />\r\n</span><span style=\"font-family: Osaka;\">&nbsp;&nbsp;</span><span style=\"font-family: 宋体;\">原味生活的食品安全监督沙龙由</span><span style=\"font-family: 宋体;\">申报的4名消费者组成，在镇江地区这个短半径供应体系内，以消费者自身的&ldquo;安全性&rdquo;为视角，定期不定期的监督检查整个农产品的生产和供应环节，对供应体系中的不合格的农户及商品具有一票否决的权利。</span></span></span></p>\r\n<p><span style=\"color: rgb(51, 51, 51);\"><span style=\"font-size: small;\"><span style=\"font-family: Osaka;\"><br />\r\n</span></span></span><span style=\"font-size: 10pt; font-family: Osaka; color: rgb(51, 51, 51); \"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top: 0pt; margin-bottom: 12pt; text-align: left; margin-left: 0pt; font-size: 10.5pt; line-height: 15.6pt; \"><span style=\"color: rgb(51, 51, 