INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('199', '17', '2013年2月出场的蔬菜新品种（已供应）', '<p><span style=\"font-size: small;\"><span style=\"color: rgb(255, 102, 0);\"><strong>&nbsp;1、樱桃萝卜（少量，会续种）：</strong></span></span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/30.jpg\" width=\"928\" height=\"619\" alt=\"\" /><br type=\"_moz\" />\r\n</span></p>\r\n<p><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"color: rgb(255, 102, 0);\"><strong>2、娃娃菜（还没全长好，因为过年，提前开吃了）：</strong></span></span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/25.jpg\" width=\"925\" height=\"616\" alt=\"\" /><br type=\"_moz\" />\r\n</span></p>\r\n<p><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"color: rgb(255, 102, 0);\"><strong>3、紫罗兰青菜，简称 紫青菜，属于特种高档菜（少量种植，续种一阵子），白色的部分是土著菌发酵后的白色菌丝：</strong></span></span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/24.jpg\" width=\"928\" height=\"613\" alt=\"\" /><br type=\"_moz\" />\r\n</span></p>\r\n<p><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"color: rgb(255, 102, 0);\"><strong>4、意大利苦苣（后续会种上巴西苦苣）：</strong></span></span></p>\r\n<p>&nbsp;</p>\r\n<p><img src=\"/images/upload/Image/2013-02-15_134256.jpg\" width=\"620\" height=\"620\" alt=\"\" /></p>', '', '', '', '0', '1', '1360041855', '', '0', 'http://', '2月已经和即将出场的蔬菜新品种');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('200', '17', '2012年夏季的传统有机肥堆肥（使用结束）', '<p><u><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">&nbsp;1、方法简述：</span></strong></span></u></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: 宋体; line-height: 18px; text-align: center;\">&nbsp; 基肥：堆肥材料</span><span lang=\"EN-US\" style=\"border: 0px; vertical-align: baseline; list-style: none; font-family: 微软雅黑, 宋体, Verdana, Arial; line-height: 18px; text-align: center;\">2</span><span style=\"font-family: 宋体; line-height: 18px; text-align: center;\">类。一类是落叶、干草（非豆科草类）、果皮、菜叶、枯枝干藤、木屑、碎木片之类富含碳元素的&ldquo;绿色材料&rdquo;；二是畜粪、禽粪、鸟粪、棉籽、豆渣、咖啡渣、海藻、豆科绿肥、豆科果荚藤蔓等富含氮元素的&ldquo;棕色肥料&rdquo;。</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: 宋体; line-height: 18px; text-align: center;\">&nbsp; 首先在地面上铺一层约为</span><span lang=\"EN-US\" style=\"border: 0px; vertical-align: baseline; list-style: none; font-family: 微软雅黑, 宋体, Verdana, Arial; line-height: 18px; text-align: center;\">15cm</span><span style=\"font-family: 宋体; line-height: 18px; text-align: center;\">厚的&ldquo;绿色材料&rdquo;。然后在上面铺一层约为</span><span lang=\"EN-US\" style=\"border: 0px; vertical-align: baseline; list-style: none; font-family: 微软雅黑, 宋体, Verdana, Arial; line-height: 18px; text-align: center;\">5cm</span><span style=\"font-family: 宋体; line-height: 18px; text-align: center;\">厚的&ldquo;棕色材料&rdquo;。</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: 宋体; line-height: 18px; text-align: center;\">&nbsp; 再在上面撒一层薄薄的腐殖土（或是完熟的堆肥）和石灰石粉。石灰石分也可用草木灰代替。这样一层层的堆至</span><span lang=\"EN-US\" style=\"border: 0px; vertical-align: baseline; list-style: none; font-family: 微软雅黑, 宋体, Verdana, Arial; line-height: 18px; text-align: center;\">1.5m</span><span style=\"font-family: 宋体; line-height: 18px; text-align: center;\">高、</span><span lang=\"EN-US\" style=\"border: 0px; vertical-align: baseline; list-style: none; font-family: 微软雅黑, 宋体, Verdana, Arial; line-height: 18px; text-align: center;\">1.5-3m</span><span style=\"font-family: 宋体; line-height: 18px; text-align: center;\">宽。</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: 宋体; line-height: 18px; text-align: center;\">&nbsp; 然后在上面盖一层厚厚的草或者土，以减少水分的蒸发。</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: 宋体; line-height: 18px; text-align: center;\">&nbsp; 堆肥时注意不要把材料踏实、要保持疏松透气。最后给堆肥浇水，要浇透，但不要变成稀烂。以后还要适当浇水，使堆肥保持湿润。在炎热干燥的天气尤其重要。</span></span></p>\r\n<p><span style=\"font-size: small;\"><span lang=\"EN-US\" style=\"border: 0px; vertical-align: baseline; list-style: none; font-family: 微软雅黑, 宋体, Verdana, Arial; line-height: 18px; text-align: center;\">&nbsp; 3</span><span style=\"font-family: 宋体; line-height: 18px; text-align: center;\">周后把堆肥翻一翻，将里面的材料翻到外面，将外面的材料翻到里面。过了</span><span lang=\"EN-US\" style=\"border: 0px; vertical-align: baseline; list-style: none; font-family: 微软雅黑, 宋体, Verdana, Arial; line-height: 18px; text-align: center;\">5</span><span style=\"font-family: 宋体; line-height: 18px; text-align: center;\">周，再把堆肥翻一翻。再过</span><span lang=\"EN-US\" style=\"border: 0px; vertical-align: baseline; list-style: none; font-family: 微软雅黑, 宋体, Verdana, Arial; line-height: 18px; text-align: center;\">4</span><span style=\"font-family: 宋体; line-height: 18px; text-align: center;\">周，所有的材料基本上充分腐烂分解了，这时堆肥就做好了，合起来差不多需要</span><span lang=\"EN-US\" style=\"border: 0px; vertical-align: baseline; list-style: none; font-family: 微软雅黑, 宋体, Verdana, Arial; line-height: 18px; text-align: center;\">3</span><span style=\"font-family: 宋体; line-height: 18px; text-align: center;\">个月时间。</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: 宋体; line-height: 18px; text-align: center;\">&nbsp; 人们通过实验发现，堆肥中的碳元素和氮元素的重量比例在</span><span lang=\"EN-US\" style=\"border: 0px; vertical-align: baseline; list-style: none; font-family: 微软雅黑, 宋体, Verdana, Arial; line-height: 18px; text-align: center;\">30:1</span><span style=\"font-family: 宋体; line-height: 18px; text-align: center;\">左右时，微生物生长繁殖最好。这个比例正好是</span><span lang=\"EN-US\" style=\"border: 0px; vertical-align: baseline; list-style: none; font-family: 微软雅黑, 宋体, Verdana, Arial; line-height: 18px; text-align: center;\">30:1</span><span style=\"font-family: 宋体; line-height: 18px; text-align: center;\">，比较合适。</span></span></p>\r\n<p>&nbsp;</p>\r\n<p><u><span style=\"font-size: small;\"><span style=\"color: rgb(255, 102, 0);\"><strong>2、过程照片：</strong></span></span></u></p>\r\n<p><span style=\"font-size: small;\">1）、<font face=\"Tahoma, Verdana, 宋体\"><span style=\"line-height: 36px;\">准备材料<b>：</b></span></font><br type=\"_moz\" />\r\n</span></p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/1(13).jpg\" width=\"820\" height=\"616\" alt=\"\" /><br type=\"_moz\" />\r\n</span></p>\r\n<p><span style=\"font-size: small;\">这次使用村上堆着不用的秸秆。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/2(14).jpg\" width=\"820\" height=\"613\" alt=\"\" /><br />\r\n</span></p>\r\n<p><span style=\"font-size: small;\">购买的鸡粪。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/3(9).jpg\" width=\"817\" height=\"610\" alt=\"\" /><br />\r\n</span></p>\r\n<p><span style=\"font-size: small;\">整过的空地。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/4(11).jpg\" width=\"814\" height=\"610\" alt=\"\" /><br />\r\n</span></p>\r\n<p><span style=\"font-size: small;\">购买的菜籽饼。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/5(11).jpg\" width=\"820\" height=\"613\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\">复合发酵菌，有点黄酒和醋味。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/6(6).jpg\" width=\"820\" height=\"613\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\">准备去抽水喷洒。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/14(5).jpg\" width=\"817\" height=\"610\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\">蜡质层很厚的小麦秸秆近拍。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\">2）、开始步骤：</span></p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/8(5).jpg\" width=\"820\" height=\"616\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/9(3).jpg\" width=\"820\" height=\"616\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\">洒湿地表。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/10(5).jpg\" width=\"820\" height=\"616\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/11(7).jpg\" width=\"817\" height=\"613\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/12(5).jpg\" width=\"820\" height=\"616\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\">均匀铺在地表。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/1(14).jpg\" width=\"820\" height=\"616\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/2(15).jpg\" width=\"820\" height=\"613\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/3(10).jpg\" width=\"820\" height=\"616\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\">开始撒发酵菌类需要的糖分（禽畜粪、菜籽饼等），辅助发酵可撒一些红糖。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/4(12).jpg\" width=\"820\" height=\"616\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\">用水喷湿润。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/5(12).jpg\" width=\"820\" height=\"610\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/6(7).jpg\" width=\"823\" height=\"613\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\">表面铺设均匀。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/7(5).jpg\" width=\"823\" height=\"613\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\">复合发酵菌稀释100倍。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/8(6).jpg\" width=\"820\" height=\"613\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\">均匀泼在秸秆和粪肥表面。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/9(4).jpg\" width=\"823\" height=\"613\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/10(6).jpg\" width=\"826\" height=\"610\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\">均匀的泼好复合发酵菌后，再次铺设秸秆。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/11(8).jpg\" width=\"817\" height=\"613\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/12(6).jpg\" width=\"826\" height=\"616\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/13(4).jpg\" width=\"823\" height=\"616\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/14(6).jpg\" width=\"820\" height=\"610\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/15(4).jpg\" width=\"823\" height=\"616\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/16.jpg\" width=\"820\" height=\"613\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/17(2).jpg\" width=\"823\" height=\"616\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/12(6).jpg\" width=\"0\" height=\"0\" alt=\"\" /></span><span style=\"font-size: small;\">循环作业，直至堆材料到1m-2m左右高度。</span></p>\r\n<p><span style=\"font-size: small;\">最后要覆盖上薄膜或者干净的塘泥，每月翻堆一次。</span></p>\r\n<p><span style=\"font-size: small;\">原因是发酵菌发酵使得堆肥中心温度升高，之后因为发酵的逐步结束而逐渐降温，此时，需要再将外部的氧气和秸秆翻入菌群中进行继续分解，周而复始。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/1(15).jpg\" width=\"820\" height=\"613\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\">这是最终堆肥状态，冉冉的热蒸汽，凝结在塑料薄膜上。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/2(17).jpg\" width=\"817\" height=\"613\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/4(13).jpg\" width=\"820\" height=\"616\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/5(13).jpg\" width=\"823\" height=\"616\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"line-height: 19px;\">温度计一测，80度不到，非常好的发酵状态！</span><br style=\"font-size: small; line-height: 19px;\" />\r\n<span style=\"line-height: 19px;\">一般70度以上，有害病菌都会被杀死。</span><br style=\"font-size: small; line-height: 19px;\" />\r\n<span style=\"line-height: 19px;\">也可以杀死大多数的小麦秸秆上携带的病菌。</span></span></p>\r\n<p>&nbsp;</p>\r\n<p>\r\n<table width=\"100%\" height=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" style=\"font-family: Tahoma, Verdana, 宋体; font-size: 12px; color: rgb(0, 0, 0); table-layout: fixed; word-wrap: break-word;\">\r\n    <tbody>\r\n        <tr>\r\n            <td valign=\"top\"><span style=\"font-size: small;\">\r\n            <div style=\"font-size: 16px; line-height: 1.5; word-wrap: break-word;\"><span style=\"color: rgb(255, 102, 0);\"><u><strong>3、最终状态及应用：</strong></u></span></div>\r\n            </span><span style=\"font-size: small;\">\r\n            <div style=\"font-size: 16px; line-height: 1.5; word-wrap: break-word;\">&nbsp;<span style=\"font-size: small;\">&nbsp;</span></div>\r\n            </span>\r\n            <div style=\"font-size: 16px; line-height: 1.5; word-wrap: break-word;\"><span style=\"font-size: small;\"><img src=\"/images/upload/Image/2(12).jpg\" width=\"820\" height=\"616\" alt=\"\" /><br type=\"_moz\" />\r\n            </span></div>\r\n            <div style=\"font-size: 16px; line-height: 1.5; word-wrap: break-word;\"><span style=\"font-size: small;\"><img src=\"/images/upload/Image/3(8).jpg\" width=\"820\" height=\"616\" alt=\"\" /><br type=\"_moz\" />\r\n            </span></div>\r\n            <div style=\"font-size: 16px; line-height: 1.5; word-wrap: break-word;\"><span style=\"font-size: small;\">已经成逐渐腐烂、接近粉末状。</span></div>\r\n            <div style=\"font-size: 16px; line-height: 1.5; word-wrap: break-word;\"><span style=\"font-size: small;\">&nbsp;</span></div>\r\n            <div style=\"font-size: 16px; line-height: 1.5; word-wrap: break-word;\"><span style=\"font-size: small;\">下田，等好收成了：</span></div>\r\n            <div style=\"font-size: 16px; line-height: 1.5; word-wrap: break-word;\"><span style=\"font-size: small;\"><img src=\"/images/upload/Image/5(9).jpg\" width=\"463\" height=\"616\" alt=\"\" /></span></div>\r\n            <div style=\"font-size: 16px; line-height: 1.5; word-wrap: break-word;\"><span style=\"font-size: small;\"><img src=\"/images/upload/Image/4(10).jpg\" width=\"823\" height=\"613\" alt=\"\" /></span><span style=\"font-size: small;\"><br />\r\n            </span></div>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</p>', '', '', '', '0', '1', '1360043363', '', '0', 'http://', '2012年夏季的传统有机肥堆肥');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('201', '17', '2013年春季的传统有机肥堆肥（正在发酵）', '<p>&nbsp;</p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span lang=\"EN-US\" style=\"font-family: 宋体;\">&nbsp;</span><strong><span style=\"font-weight: inherit; font-style: inherit;\"><u><span style=\"font-style: inherit;\"><span lang=\"EN-US\" style=\"font-family: 宋体; color: rgb(255, 102, 0); border: 1pt none windowtext; padding: 0cm;\">1</span></span></u></span></strong><strong><u><span style=\"font-family: 宋体; color: rgb(255, 102, 0); border: 1pt none windowtext; padding: 0cm;\">、方法简述（方法和<span lang=\"EN-US\">2012</span>年一致）：</span></u></strong></span><span lang=\"EN-US\" style=\"font-size: 12pt; font-family: 宋体;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span lang=\"EN-US\" style=\"font-family: 宋体;\">&nbsp;</span></span><span lang=\"EN-US\" style=\"font-size: 12pt; font-family: 宋体;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span style=\"font-style: inherit;\"><span style=\"font-style: inherit;\"><span lang=\"EN-US\" style=\"font-family: 宋体; color: rgb(102, 102, 102); border: 1pt none windowtext; padding: 0cm;\">&nbsp; </span><span style=\"font-family: 宋体; color: rgb(102, 102, 102); border: 1pt none windowtext; padding: 0cm;\">基肥：堆肥材料</span><span lang=\"EN-US\"><span style=\"font-style: inherit;\">2</span></span><span style=\"font-style: inherit;\">类。一类是落叶、干草（非豆科草类）、果皮、菜叶、枯枝干藤、木屑、碎木片之类富含碳元素的&ldquo;绿色材料&rdquo;；二是畜粪、禽粪、鸟粪、棉籽、豆渣、咖啡渣、海藻、豆科绿肥、豆科果荚藤蔓等富含氮元素的&ldquo;棕色肥料&rdquo;。</span></span></span></span><span lang=\"EN-US\" style=\"font-size:12.0pt;font-family:宋体;mso-bidi-font-family:宋体;\r\ncolor:#666666\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span style=\"font-style: inherit;\"><span style=\"font-style: inherit;\"><span lang=\"EN-US\" style=\"font-family: 宋体; color: rgb(102, 102, 102); border: 1pt none windowtext; padding: 0cm;\">&nbsp; </span><span style=\"font-family: 宋体; color: rgb(102, 102, 102); border: 1pt none windowtext; padding: 0cm;\">首先在地面上铺一层约为</span><span lang=\"EN-US\"><span style=\"font-style: inherit;\">15cm</span></span><span style=\"font-style: inherit;\">厚的&ldquo;绿色材料&rdquo;。然后在上面铺一层约为</span><span lang=\"EN-US\"><span style=\"font-style: inherit;\">5cm</span></span><span style=\"font-style: inherit;\">厚的&ldquo;棕色材料&rdquo;。</span></span></span></span><span lang=\"EN-US\" style=\"font-size:12.0pt;font-family:宋体;color:#666666\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span style=\"font-style: inherit;\"><span style=\"font-style: inherit;\"><span lang=\"EN-US\" style=\"font-family: 宋体; color: rgb(102, 102, 102); border: 1pt none windowtext; padding: 0cm;\">&nbsp; </span><span style=\"font-family: 宋体; color: rgb(102, 102, 102); border: 1pt none windowtext; padding: 0cm;\">再在上面撒一层薄薄的腐殖土（或是完熟的堆肥）和石灰石粉。石灰石分也可用草木灰代替。这样一层层的堆至</span><span lang=\"EN-US\"><span style=\"font-style: inherit;\">1.5m</span></span><span style=\"font-style: inherit;\">高、</span><span lang=\"EN-US\"><span style=\"font-style: inherit;\">1.5-3m</span></span><span style=\"font-style: inherit;\">宽。</span></span></span></span><span lang=\"EN-US\" style=\"font-size:12.0pt;font-family:宋体;color:#666666\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span style=\"font-style: inherit;\"><span style=\"font-style: inherit;\"><span lang=\"EN-US\" style=\"font-family: 宋体; color: rgb(102, 102, 102); border: 1pt none windowtext; padding: 0cm;\">&nbsp; </span><span style=\"font-family: 宋体; color: rgb(102, 102, 102); border: 1pt none windowtext; padding: 0cm;\">然后在上面盖一层厚厚的草或者土，以减少水分的蒸发。</span></span></span></span><span lang=\"EN-US\" style=\"font-size:12.0pt;font-family:宋体;color:#666666\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span style=\"font-style: inherit;\"><span style=\"font-style: inherit;\"><span lang=\"EN-US\" style=\"font-family: 宋体; color: rgb(102, 102, 102); border: 1pt none windowtext; padding: 0cm;\">&nbsp; </span><span style=\"font-family: 宋体; color: rgb(102, 102, 102); border: 1pt none windowtext; padding: 0cm;\">堆肥时注意不要把材料踏实、要保持疏松透气。最后给堆肥浇水，要浇透，但不要变成稀烂。以后还要适当浇水，使堆肥保持湿润。在炎热干燥的天气尤其重要。</span></span></span></span><span lang=\"EN-US\" style=\"font-size:12.0pt;font-family:宋体;color:#666666\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span style=\"font-style: inherit;\"><span style=\"font-style: inherit;\"><span lang=\"EN-US\" style=\"font-family: 宋体; color: rgb(102, 102, 102); border: 1pt none windowtext; padding: 0cm;\">&nbsp; 3</span></span><span style=\"font-style: inherit;\"><span style=\"font-family: 宋体; color: rgb(102, 102, 102); border: 1pt none windowtext; padding: 0cm;\">周后把堆肥翻一翻，将里面的材料翻到外面，将外面的材料翻到里面。过了</span><span lang=\"EN-US\"><span style=\"font-style: inherit;\">5</span></span><span style=\"font-style: inherit;\">周，再把堆肥翻一翻。再过</span><span lang=\"EN-US\"><span style=\"font-style: inherit;\">4</span></span><span style=\"font-style: inherit;\">周，所有的材料基本上充分腐烂分解了，这时堆肥就做好了，合起来差不多需要</span><span lang=\"EN-US\"><span style=\"font-style: inherit;\">3</span></span><span style=\"font-style: inherit;\">个月时间。</span></span></span></span><span lang=\"EN-US\" style=\"font-size:12.0pt;font-family:宋体;color:#666666\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span style=\"font-style: inherit;\"><span style=\"font-style: inherit;\"><span lang=\"EN-US\" style=\"font-family: 宋体; color: rgb(102, 102, 102); border: 1pt none windowtext; padding: 0cm;\">&nbsp; </span><span style=\"font-family: 宋体; color: rgb(102, 102, 102); border: 1pt none windowtext; padding: 0cm;\">人们通过实验发现，堆肥中的碳元素和氮元素的重量比例在</span><span lang=\"EN-US\"><span style=\"font-style: inherit;\">30:1</span></span><span style=\"font-style: inherit;\">左右时，微生物生长繁殖最好。这个比例正好是</span><span lang=\"EN-US\"><span style=\"font-style: inherit;\">30:1</span></span><span style=\"font-style: inherit;\">，比较合适。</span></span></span></span><span lang=\"EN-US\" style=\"font-size:12.0pt;font-family:宋体;color:#666666\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span lang=\"EN-US\" style=\"font-family: 宋体; color: rgb(102, 102, 102);\">&nbsp;</span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><strong><span lang=\"EN-US\" style=\"font-family: 宋体; color: rgb(255, 102, 0);\">2</span></strong><strong><span style=\"font-family: 宋体; color: rgb(255, 102, 0);\">、过程描述（过程和<span lang=\"EN-US\">2012</span>年一致，但是有一些小的改进）：</span></strong></span><span lang=\"EN-US\" style=\"font-size:12.0pt;font-family:宋体;color:#666666\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span lang=\"EN-US\" style=\"font-family: 宋体; color: rgb(102, 102, 102);\">&nbsp;</span></span><span lang=\"EN-US\" style=\"font-size:12.0pt;font-family:宋体;color:#666666\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><strong><span style=\"font-family: 宋体; color: rgb(102, 102, 102);\">（<span lang=\"EN-US\">1</span>）周边的杂草和秸秆均回收利用了</span></strong></span><span lang=\"EN-US\" style=\"font-size:12.0pt;font-family:宋体;color:#666666\"><o:p></o:p></span></p>\r\n<p>&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px; border: 0px; list-style: none; vertical-align: baseline; font-family: 微软雅黑, 宋体, Verdana, Arial; color: rgb(102, 102, 102); line-height: 18px;\"><span style=\"font-size: small;\"><img src=\"/images/upload/Image/1(12).jpg\" width=\"922\" height=\"613\" alt=\"\" /></span></p>\r\n<p style=\"margin: 0px; padding: 0px; border: 0px; list-style: none; vertical-align: baseline; font-family: 微软雅黑, 宋体, Verdana, Arial; color: rgb(102, 102, 102); line-height: 18px;\"><span style=\"font-size: small;\"><img src=\"/images/upload/Image/1(16).jpg\" width=\"820\" height=\"613\" alt=\"\" /></span></p>\r\n<p style=\"margin: 0px; padding: 0px; border: 0px; list-style: none; vertical-align: baseline; font-family: 微软雅黑, 宋体, Verdana, Arial; color: rgb(102, 102, 102); line-height: 18px;\"><span style=\"font-size: small;\">&nbsp;</span></p>\r\n<p style=\"margin: 0px; padding: 0px; border: 0px; list-style: none; vertical-align: baseline; font-family: 微软雅黑, 宋体, Verdana, Arial; color: rgb(102, 102, 102); line-height: 18px;\">&nbsp;</p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><strong><span style=\"font-family: 宋体;\">（<span lang=\"EN-US\">2</span>）禽畜、人粪尿均使用了：</span></strong></span><span lang=\"EN-US\" style=\"font-size:12.0pt;font-family:宋体\"><o:p></o:p></span></p>\r\n<p>&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px; border: 0px; list-style: none; vertical-align: baseline; font-family: 微软雅黑, 宋体, Verdana, Arial; color: rgb(102, 102, 102); line-height: 18px;\"><span style=\"font-size: small;\"><img src=\"/images/upload/Image/12(4).jpg\" width=\"820\" height=\"616\" alt=\"\" /></span></p>\r\n<p style=\"margin: 0px; padding: 0px; border: 0px; list-style: none; vertical-align: baseline; font-family: 微软雅黑, 宋体, Verdana, Arial; color: rgb(102, 102, 102); line-height: 18px;\"><span style=\"font-size: small;\"><img src=\"/images/upload/Image/3(12).jpg\" width=\"820\" height=\"613\" alt=\"\" /></span></p>\r\n<p style=\"margin: 0px; padding: 0px; border: 0px; list-style: none; vertical-align: baseline; font-family: 微软雅黑, 宋体, Verdana, Arial; color: rgb(102, 102, 102); line-height: 18px;\"><span style=\"font-size: small;\">&nbsp;</span></p>\r\n<p style=\"margin: 0px; padding: 0px; border: 0px; list-style: none; vertical-align: baseline; font-family: 微软雅黑, 宋体, Verdana, Arial; color: rgb(102, 102, 102); line-height: 18px;\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p><span style=\"font-size: small;\">\r\n<p class=\"MsoNormal\"><strong><span style=\