INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('202', '18', '洋鸡和土鸡的区别（图解） ————重点推荐！', '<p><span style=\"font-size: small;\">&nbsp;<img src=\"/images/upload/Image/1(17).jpg\" width=\"715\" height=\"538\" alt=\"\" /></span></p>\r\n<p>&nbsp;<span style=\"font-size: small;\"> </span><span style=\"color: rgb(255, 0, 0);\"><strong>区别一：洋鸡毛色自然没有土鸡毛色鲜艳！</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><img src=\"/images/upload/Image/2(19).jpg\" width=\"718\" height=\"538\" alt=\"\" /></span></p>\r\n<p><span style=\"color: rgb(255, 0, 0);\"><strong>区别二：洋鸡因为饲料喂养，则肤色偏白；土鸡因为食用五谷杂粮，则肤色偏黄色！</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><img src=\"/images/upload/Image/3(13).jpg\" width=\"718\" height=\"367\" alt=\"\" /></p>\r\n<p><strong style=\"color: rgb(255, 0, 0);\">区别三：洋鸡鸡胗较小、生长期较短，所以鸡胗内金黄色；土鸡鸡胗较大、生长期较长，所以鸡胗内金黄色较深！</strong></p>', '', '', '', '0', '1', '1360473359', '', '0', 'http://', '洋鸡和土鸡的区别（图解）');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('203', '18', '大白菜的冬季储存方法', '<p><span style=\"font-size: small;\"><strong>地窖储存白菜和胡萝卜的方法：</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"> </span></p>\r\n<p><span style=\"font-family: Tahoma, Helvetica, SimSun, sans-serif; line-height: 21px;\">（1）挖窖时，地表土单放一旁，来年回填时仍在上面；</span><br style=\"word-wrap: break-word; color: rgb(68, 68, 68); font-family: Tahoma, Helvetica, SimSun, sans-serif; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"font-family: Tahoma, Helvetica, SimSun, sans-serif; line-height: 21px;\">（2）每户一口窖足够，一头白菜，一头萝卜或胡萝卜；</span><br style=\"word-wrap: break-word; color: rgb(68, 68, 68); font-family: Tahoma, Helvetica, SimSun, sans-serif; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"font-family: Tahoma, Helvetica, SimSun, sans-serif; line-height: 21px;\">（3）窖盖可用旧毯子、旧被单等，也可用塑料，但必须留透气孔；</span><br style=\"word-wrap: break-word; color: rgb(68, 68, 68); font-family: Tahoma, Helvetica, SimSun, sans-serif; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"font-family: Tahoma, Helvetica, SimSun, sans-serif; line-height: 21px;\">（4）胡萝卜从地里往外挖出时不要弄破皮，破皮就流失了营养，窖储时缨子用刀齐脖切掉，别伤果实；</span></p>\r\n<p>&nbsp;&nbsp;<img src=\"/images/upload/Image/7(6).jpg\" alt=\"\" /></p>\r\n<p><span style=\"font-size: small;\"> </span></p>\r\n<p><br style=\"word-wrap: break-word; color: rgb(68, 68, 68); font-family: Tahoma, Helvetica, SimSun, sans-serif; font-size: 14px; line-height: 21px;\" />\r\n<strong><span style=\"font-family: Tahoma, Helvetica, SimSun, sans-serif; line-height: 21px;\">阳台储存白菜，有如下几种方法：</span></strong><br style=\"word-wrap: break-word; color: rgb(68, 68, 68); font-family: Tahoma, Helvetica, SimSun, sans-serif; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"font-family: Tahoma, Helvetica, SimSun, sans-serif; line-height: 21px;\">（1）立放纸箱内，一周活动一次；</span><br style=\"word-wrap: break-word; color: rgb(68, 68, 68); font-family: Tahoma, Helvetica, SimSun, sans-serif; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"font-family: Tahoma, Helvetica, SimSun, sans-serif; line-height: 21px;\">（2）卧放纸壳或木板上(不可放瓷砖上)，层与层中间用木棒（木条、竹棍）等隔开使透气；</span><br style=\"word-wrap: break-word; color: rgb(68, 68, 68); font-family: Tahoma, Helvetica, SimSun, sans-serif; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"font-family: Tahoma, Helvetica, SimSun, sans-serif; line-height: 21px;\">（3）定期查看，两周捣腾一次；</span><br style=\"word-wrap: break-word; color: rgb(68, 68, 68); font-family: Tahoma, Helvetica, SimSun, sans-serif; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"font-family: Tahoma, Helvetica, SimSun, sans-serif; line-height: 21px;\">（4）没前阳台的朋友，后阳台放到菜叶冰手时（外面零下6-7度，约12月中旬），移到厨房内（其实已所剩无几了）；</span><br style=\"word-wrap: break-word; color: rgb(68, 68, 68); font-family: Tahoma, Helvetica, SimSun, sans-serif; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"font-family: Tahoma, Helvetica, SimSun, sans-serif; line-height: 21px;\">（5）把菜择干净，用塑料袋缠住菜身，露出上端菜叶，用透明胶捆住，这样挨排放就不用木条木棍了。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><span style=\"color: rgb(68, 68, 68); font-family: Tahoma, Helvetica, SimSun, sans-serif; line-height: 21px;\"><br />\r\n</span></span></p>', '', '', '', '0', '1', '1360474938', '', '0', 'http://', '');
