INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('216', '12', '感兴趣的朋友，可以用test用户进入看看，详情请点击！', '<p><img src=\"/images/upload/Image/head.jpg\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\">（1）对种植过程0农药有机肥的蔬菜套餐感兴趣的人，可以用以下的信息登陆一下:<br type=\"_moz\" />\r\n</span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"color: rgb(255, 0, 0);\"><strong>用户名： test</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"color: rgb(255, 0, 0);\"><strong>密码：111111</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"color: rgb(51, 51, 51);\">（2）如何操作，请参考<strong>：</strong></span></span><span style=\"font-size: small;\"><span style=\"color: rgb(255, 0, 0);\"><strong><a href=\"http://yw0511.com/article-179.html\"><span style=\"color: rgb(255, 0, 0);\"><strong>蔬菜套餐操作界面解说</strong></span></a></strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><br />\r\n</span></p>\r\n<p><span style=\"font-size: small;\">（3）有不懂的，请尽管询问顶部和左下的客服。</span></p>', '', '', '', '0', '1', '1361752337', '', '0', 'http://', '');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('217', '18', '如何挑选老母鸡 ————重点推荐！', '<p>&nbsp;<img src=\"/images/upload/Image/003.jpg\" alt=\"\" /></p>\r\n<p><span style=\"color: rgb(51, 51, 51);\"><span style=\"font-size: small;\"><span style=\"font-family: Verdana, Arial, Tahoma; line-height: 28px; background-color: rgb(247, 252, 255);\">挑选老母鸡三招众所周知，老母鸡是生小孩过后非常好的进补食物，许多人家都开始买老母鸡，煲老鸡汤进补，不过，在买老鸡时，许多人常常是花了老鸡的钱，买回来的却不是老鸡。</span></span></span></p>\r\n<p><span style=\"color: rgb(51, 51, 51);\"><span style=\"font-size: small;\"><span style=\"font-family: Verdana, Arial, Tahoma; line-height: 28px; background-color: rgb(247, 252, 255);\">那么，如何识别老母鸡呢？</span></span></span></p>\r\n<p><span style=\"color: rgb(51, 51, 51);\"><span style=\"font-size: small;\"><span style=\"font-family: Verdana, Arial, Tahoma; line-height: 28px; background-color: rgb(247, 252, 255);\">下面我教大家识别老母鸡三招：</span></span></span></p>\r\n<p><span style=\"color: rgb(255, 0, 0);\"><strong><span style=\"font-size: small;\"><span style=\"font-family: Verdana, Arial, Tahoma; line-height: 28px; background-color: rgb(247, 252, 255);\">一看鸡冠。老母鸡的鸡冠一般比较大，而且，鸡冠软绵绵的，色泽不太鲜艳。如果是这种鸡冠，可初步确定是老母鸡。</span></span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 0, 0);\"><strong><span style=\"font-size: small;\"><span style=\"font-family: Verdana, Arial, Tahoma; line-height: 28px; background-color: rgb(247, 252, 255);\">二看鸡爪。老母鸡的鸡爪比较锋利，再有老母鸡的鸡腿上的皮比较粗、老，如果是这种鸡爪，可初步确定是老母鸡。</span></span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 0, 0);\"><strong><span style=\"font-size: small;\"><span style=\"font-family: Verdana, Arial, Tahoma; line-height: 28px; background-color: rgb(247, 252, 255);\">三摸鸡屁股。看一个鸡是不是老母鸡，可用手摸鸡屁股，老母鸡的屁股突起部分比较大。如果是这种鸡屁股，可初步确定是老母鸡。如果鸡冠、鸡爪、鸡屁股都符合老母鸡的特征，就可以确定是老母鸡，放心买就是了。</span></span></strong></span></p>', '', '', '', '0', '1', '1361754654', '', '0', 'http://', '');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('218', '18', '如何购买放心猪肉 ————重点推荐！', '<p><img src=\"/images/upload/Image/as1.jpg\" alt=\"\" /></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: 宋体; line-height: 20px; text-indent: 2em;\">在目前猪肉价格偏高的特殊时期，避免劣质猪肉的上市，如何购买放心肉是非常重要的问题。提醒广大消费者，对放心猪肉的鉴别做到以下几点：</span></span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"color: rgb(0, 0, 0);\"><span style=\"font-size: small;\">一、看清标志和证明</span></span></p>\r\n<p><span style=\"font-size: small;\">放心肉是由政府指定的定点屠宰企业加工的，有屠宰企业和动物监督部门出具的《肉品检验合格证明》、《动物产品检疫合格证明》，并且在胴体表面加盖检验、检疫印章。</span></p>\r\n<p>&nbsp;</p>\r\n<p style=\"text-align: left;\"><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-size: small;\"><span style=\"color: rgb(0, 0, 0);\">二、懂得放心肉特征</span></span></span></p>\r\n<p><span style=\"font-size: small;\">放心肉是在定点屠宰企业经流水线屠宰加工的，经过了刺杀放血、烫毛、脱毛、净膛、分割、修剪等程序，<span style=\"color: rgb(255, 0, 0);\"><strong>外表整洁美观无猪毛和血迹遗留，胴体表面盖有动物检疫部门和屠宰企业的印章</strong></span>。在猪的两后肢附近的淋巴结和头部的下颌淋巴结已被动物检验人员切开，并且肾脏有检查刀口。<span style=\"color: rgb(255, 0, 0);\"><strong>脊柱经过分割机的切割，切面整齐平整，而私宰肉是用刀分割的，表面粗糙不平</strong></span>。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"color: rgb(0, 0, 0);\"><span style=\"font-size: small;\">三、知道如何判断新鲜猪肉</span></span></p>\r\n<p><span style=\"font-size: small;\">对新鲜猪肉质地的判断做到一看、二触、三嗅：</span></p>\r\n<p><span style=\"font-size: small;\">一看就是看猪肉的外表，<span style=\"color: rgb(255, 0, 0);\"><strong>正常新鲜的猪肉表面有层微干或微湿润的薄膜，肌肉有光泽，瘦肉部分红色均匀、脂肪洁白，外表微干或微湿润</strong></span>。<span style=\"color: rgb(255, 0, 0);\"><strong>病死猪肉外观呈暗红色，皮肉、脂肪、内脏有出血点及血斑，有的还有紫斑，脂肪颜色变黄，骨髓为黑色，细看之下，有的毛细血管内还有淤血；健康猪肉呈白色或浅白色，切面有光泽，棕色或粉红色，无任何液体流出；病死猪的肉肌肉无弹性，切面光滑、暗紫色，平切面有淡黄色或粉红色液体。为防止不法商贩在检疫合格的肉品中插入病死猪肉，要检查猪肉和内脏，凡是发现猪肉皮肤有多量的紫红色或暗红色出血点，出血斑，肉泛红并起块状，脂肪呈红色，或内脏有严重充血、肿大或出血点、出血斑，或猪蹄壳周围的蹄冠、蹄叉出现糜烂的应可疑为病猪肉，不要购买</strong></span>。</span></p>\r\n<p><span style=\"font-size: small;\">二触就是检查猪肉组织状态，<span style=\"color: rgb(255, 0, 0);\"><strong>新鲜猪肉纤维清晰，有坚韧性，表面微干或湿润，不粘手，指压后凹陷立即恢复。病死猪肉呈暗红色，肌肉间毛细血管淤血，无弹性，指压凹下后不弹起</strong></span>。</span></p>\r\n<p><span style=\"font-size: small;\">三嗅就是嗅新鲜猪肉的气味，<span style=\"color: rgb(255, 0, 0);\"><strong>新鲜猪肉具有鲜猪肉特有的正常气味，无异味，煮沸后肉汤透明澄清，脂肪团聚表面，有香味，病死猪肉有血腥味、尿臊味、腐败味，肉汤极混浊，汤内漂浮着絮状的烂肉片，汤表面几乎无油滴，具有浓烈的腐败臭味</strong></span>。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"color: rgb(0, 0, 0);\"><span style=\"font-size: small;\">四、做到四个认清</span></span></p>\r\n<p><span style=\"font-size: small;\">(一)认清变质猪肉。<span style=\"color: rgb(255, 0, 0);\"><strong>变质猪肉无光泽，呈暗灰色和淡绿色，脂肪呈灰绿色，外表粘手，新切面发暗，指压后不能恢复原状，且有臭味，绝对不可以食用</strong></span>。</span></p>\r\n<p><span style=\"font-size: small;\">(二)认清死猪肉。<span style=\"color: rgb(255, 0, 0);\"><strong>死猪肉脂肪灰红，肌血呈黑红色，且带有蓝紫色彩。切面有黑红色的血液浸润，并流出血滴。切开后腿内部的大血管，可以挤出黑红色的血栓来，剥开板油，可见腹膜上有黑紫色的毛细血管网，切开肾包膜扒出肾脏，可以看到局部变绿，嗅嗅有腐败气味</strong></span>。</span></p>\r\n<p><span style=\"font-size: small;\">(三)认清注水猪肉。<span style=\"color: rgb(255, 0, 0);\"><strong>注水猪肉看上去肉嫩而且表面湿润，不具有正常猪肉的鲜红色和弹性，而呈淡红色，肉表面光亮。在放肉的案板上把肉移开，下边特别潮湿，甚至积水，将肉吊挂起来会往下滴水。正常猪肉的皮下脂肪和板油质地洁白，而注水猪肉的皮下脂肪和板油轻度充血，呈粉红色，新鲜切面的小血管有血水流出</strong></span>。</span></p>\r\n<p><span style=\"font-size: small;\">(四)认清公母猪肉。<span style=\"color: rgb(255, 0, 0);\"><strong>公母猪肉皮肤比较粗糙，松弛而缺乏弹性，多皱襞，且较厚，毛孔粗，并且其肉的色泽较深，呈深红色，肌纤维较粗，肌间夹杂的脂肪少，有较多的白色疏松结缔组织。母猪的皮与脂肪之间常见有一薄层呈粉红色，俗称&ldquo;红线&rdquo;。公猪肉一般有性气味，且以脂肪和臀部肌肉明显，可以用嗅检的方法鉴别</strong></span>。</span></p>\r\n<p>&nbsp;</p>\r\n<p>附：</p>\r\n<p><img src=\"/images/upload/Image/as3.jpg\" width=\"703\" height=\"436\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p><img src=\"/images/upload/Image/as4.jpg\" width=\"673\" height=\"583\" alt=\"\" /></p>\r\n<p>&nbsp;</p>', '', '', '', '0', '1', '1361769436', '', '0', 'http://', '');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('220', '18', '十渡农家卤水豆腐的制作全过程', '<p><img src=\"/images/upload/Image/ad1(1).jpg\" width=\"639\" height=\"852\" alt=\"\" /></p>\r\n<p>\r\n<p class=\"MsoNormal\"><span style=\"font-family:宋体\">这豆腐，看着就香就嫩吧。今年咱就给您介绍下十渡农家卤水豆腐的制作方法：<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\" style=\"font-family:宋体\">1.</span><span style=\"font-family:宋体\">选本地产上等黄豆，用温水浸泡至少<span lang=\"EN-US\">12</span>小时，同时要把豆腐包（包豆腐的布）洗净；<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\" style=\"font-family:宋体\">2.</span><span style=\"font-family:宋体\">在石磨上将泡过的黄豆碾碎成浆，下图中即为咱农村的石。更传统的磨橼都是木头做的。<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n</p>\r\n<p><img src=\"/images/upload/Image/ad2(1).jpg\" width=\"639\" height=\"852\" alt=\"\" /></p>\r\n<p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\" style=\"font-family:宋体\">3.</span><span style=\"font-family:宋体\">把灶火烧好，大柴锅里放上水，并将黄豆磨好的浆放在锅中熬开。在此过程中要注意，千万不能糊锅。<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n</p>\r\n<p><img src=\"/images/upload/Image/ad3(1).jpg\" alt=\"\" /></p>\r\n<p><img src=\"/images/upload/Image/ad4(1).jpg\" width=\"639\" height=\"479\" alt=\"\" /></p>\r\n<p><span lang=\"EN-US\" style=\"font-family: 宋体;\">4.</span><span style=\"font-family: 宋体;\">豆浆煮沸后，放入卤水点豆腐。这可是技术，豆腐的好坏在此是关键。</span></p>\r\n<p><img src=\"/images/upload/Image/ad5(2).jpg\" alt=\"\" /></p>\r\n<p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\" style=\"font-family:宋体\">5.</span><span style=\"font-family:宋体\">点入卤水后，即出现了豆腐脑，爱喝豆腐脑的朋友，在这个时候可盛出一大锅，放点老醋，酱水，香油，香菜，喷香啊。<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family:宋体\">将豆腐包放在一个笸箩里，铺好，将锅里的水和豆腐脑都倒在这豆腐包里，水就流出了豆腐包，倒的同时把这些水淘出笸箩，但也不能淘净。<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n</p>\r\n<p><img src=\"/images/upload/Image/ad6(1).jpg\" alt=\"\" /></p>\r\n<p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\" style=\"font-family:宋体\">6.</span><span style=\"font-family:宋体\">下面的步骤叫包豆腐和压豆腐。作用是让豆腐脑形成豆腐，同时保持一定弹性。<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n</p>\r\n<p><img src=\"/images/upload/Image/ad7(2).jpg\" width=\"639\" height=\"852\" alt=\"\" /></p>\r\n<p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\" style=\"font-family:宋体\">7.</span><span style=\"font-family:宋体\">大功告成，切块豆腐吃，趁热，什么都不放就非常香<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n</p>\r\n<p><img src=\"/images/upload/Image/ad8(1).jpg\" alt=\"\" /></p>\r\n<p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\" style=\"font-family:宋体\">8.</span><span style=\"font-family:宋体\">最后再来一张咱家这超级棒的卤水豆腐的照片：</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family:宋体\"><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n</p>\r\n<p><img src=\"/images/upload/Image/ad9(2).jpg\" alt=\"\" /></p>\r\n<p>&nbsp;</p>', '', '', '', '0', '1', '1361940866', '', '0', 'http://', '');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('221', '18', '石膏豆腐制作全过程', '<p><span style=\"font-size: small;\">&nbsp;<span style=\"color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; line-height: 23px;\">上次和某人合作了盐卤豆腐，这次再来个石膏豆腐：</span></span></p>\r\n<p><img src=\"/images/upload/Image/da1.jpg\" width=\"480\" height=\"360\" alt=\"\" /></p>\r\n<p><img src=\"/images/upload/Image/da2.jpg\" alt=\"\" /></p>\r\n<p><span style=\"font-size: small;\">石膏豆腐和盐卤豆腐的区别只在于点制原料的不同<br style=\"margin: 0px; padding: 0px;\" />\r\n这一次的材料：大豆300克，水适量，石膏15克<br style=\"margin: 0px; padding: 0px;\" />\r\n大豆事先泡制，豆浆机打浆</span></p>\r\n<p><img src=\"/images/upload/Image/da3.jpg\" alt=\"\" /></p>\r\n<p><span style=\"font-size: small;\">滤浆，这一次的豆浆浓稠了许多</span></p>\r\n<p><img src=\"/images/upload/Image/da4.jpg\" alt=\"\" /></p>\r\n<p><span style=\"font-size: small;\">滤 好 豆 浆，开 始 配 制 石 膏 水，石 膏 粉 取 15克，用 适 量 水 调 开</span></p>\r\n<p><img src=\"/images/upload/Image/da5.jpg\" alt=\"\" /></p>\r\n<p><span style=\"font-size: small;\">豆浆稍凉，结出了一层豆皮，可是咱今天要做的是豆腐，不是豆皮，把这一层揭起来，当场吃掉</span></p>\r\n<p><img src=\"/images/upload/Image/da6.jpg\" alt=\"\" /></p>\r\n<p><span style=\"font-size: small;\">这就开始点豆腐了，石膏水缓缓倒入豆浆的同时，用勺轻轻搅动，让豆浆与石膏水充分混合<br style=\"margin: 0px; padding: 0px;\" />\r\n蛋白质开始沉淀析出，静置半小时后，蛋白质完全沉淀</span></p>\r\n<p><img src=\"/images/upload/Image/da7.jpg\" width=\"480\" height=\"360\" alt=\"\" /></p>\r\n<p><span style=\"font-size: small;\">装模整形，待水过滤得差不多了，开始压制</span></p>\r\n<p><img src=\"/images/upload/Image/da8.jpg\" width=\"480\" height=\"360\" alt=\"\" /></p>\r\n<p><span style=\"font-size: small;\">喂！你没发现这个模具的机巧吗？两个重叠压制，这样不是很便利吗？</span></p>\r\n<p><img src=\"/images/upload/Image/da9.jpg\" width=\"480\" height=\"640\" alt=\"\" /></p>\r\n<p><span style=\"font-size: small;\">脱模</span></p>\r\n<p><img src=\"/images/upload/Image/da10.jpg\" width=\"480\" height=\"360\" alt=\"\" /></p>\r\n<p><img src=\"/images/upload/Image/da11.jpg\" width=\"480\" height=\"360\" alt=\"\" /></p>\r\n<p><span style=\"font-size: small;\">那是相当的成功！</span></p>\r\n<p><img src=\"/images/upload/Image/da12.jpg\" alt=\"\" /></p>\r\n<p><img src=\"/images/upload/Image/da13.jpg\" width=\"480\" height=\"360\" alt=\"\" /></p>', '', '', '', '0', '1', '1361941285', '', '0', 'http://', '');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('222', '18', '家庭自制内酯豆腐 ', '<p>&nbsp;<span lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family: 宋体;\">豆腐是营养食品，老少皆宜，豆腐是我家餐桌上的常客。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;\">这是我和先生合作酿制的一碗豆腐脑。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;<img src=\"/images/upload/Image/ac1.jpg\" width=\"749\" height=\"561\" alt=\"\" /><!--[endif]--></span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;\">我和先生在菜市场买了一包葡萄糖内酯，按说明开始学做内酯豆腐，经过失败，总结，到成功。现在我把制作过程拿出晒晒。</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;\r\nmso-hansi-font-family:&quot;Times New Roman&quot;\">材料：</span><span lang=\"EN-US\">8</span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;\">两好黄豆，</span><span lang=\"EN-US\">2500mL</span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;\">清水，</span><span lang=\"EN-US\">5mL</span><span style=\"font-family:\r\n宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:&quot;Times New Roman&quot;\">内酯，豆浆机一台。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;\">制作方法：</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;\">（</span><span lang=\"EN-US\">1</span><span style=\"font-family:\r\n宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:&quot;Times New Roman&quot;\">）</span><span lang=\"EN-US\">8</span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;\r\nmso-hansi-font-family:&quot;Times New Roman&quot;\">两好黄豆，浸泡两天，中间换两到三次水，避免黄豆出现异味。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;\">（</span><span lang=\"EN-US\">2</span><span style=\"font-family:\r\n宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:&quot;Times New Roman&quot;\">）</span><span lang=\"EN-US\">8</span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;\r\nmso-hansi-font-family:&quot;Times New Roman&quot;\">两黄豆和</span><span lang=\"EN-US\">2500mL</span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;\">清水，磨成豆浆，用布过滤分出豆浆和豆渣。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;\">（</span><span lang=\"EN-US\">3</span><span style=\"font-family:\r\n宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:&quot;Times New Roman&quot;\">）盆内放</span><span lang=\"EN-US\">5mL</span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;\r\nmso-hansi-font-family:&quot;Times New Roman&quot;\">内酯用少量温水稀释。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;\">（</span><span lang=\"EN-US\">4</span><span style=\"font-family:\r\n宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:&quot;Times New Roman&quot;\">）豆浆用小中火慢慢熬煮撇去浮沫，搅动煮沸，将煮好的豆浆倒入内酯盆中，盖上盖儿约</span><span lang=\"EN-US\">5</span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;\r\nmso-hansi-font-family:&quot;Times New Roman&quot;\">分钟成型，嫩豆腐做好既可食用。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;\">操作步骤见图示：</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\"><img src=\"/images/upload/Image/ac2.jpg\" alt=\"\" /><br />\r\n<!--[endif]--></span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;\r\nmso-hansi-font-family:&quot;Times New Roman&quot;\">这是浸泡好的黄豆</span><span lang=\"EN-US\">&nbsp;&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\"><img src=\"/images/upload/Image/ac3.jpg\" width=\"749\" height=\"561\" alt=\"\" /><br />\r\n<!--[endif]--></span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;8</span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;\r\nmso-hansi-font-family:&quot;Times New Roman&quot;\">两黄豆分两次打浆，水和黄豆磨制半分钟，挤出豆浆，豆渣加水再磨半分钟即可。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\"><img src=\"/images/upload/Image/ac4.jpg\" alt=\"\" /><br />\r\n<!--[endif]--></span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\"><img src=\"/images/upload/Image/ac5.jpg\" alt=\"\" /><br />\r\n<!--[endif]--></span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;<img src=\"/images/upload/Image/ac6.jpg\" alt=\"\" /><!--[endif]--></span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;\r\nmso-hansi-font-family:&quot;Times New Roman&quot;\">豆浆中小火熬煮。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\"><img src=\"/images/upload/Image/ac7.jpg\" width=\"749\" height=\"561\" alt=\"\" /><br />\r\n<!--[endif]--></span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;\r\nmso-hansi-font-family:&quot;Times New Roman&quot;\">煮沸的豆浆倒入用温水稀湿好的内酯盆里。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\"><img src=\"/images/upload/Image/ac8.jpg\" width=\"749\" height=\"561\" alt=\"\" /><br />\r\n<!--[endif]--></span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;\">盆里是</span><span lang=\"EN-US\">5mL</span><span style=\"font-family:\r\n宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:&quot;Times New Roman&quot;\">内酯，另加一些粉芡（也可不加）。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\"><img src=\"/images/upload/Image/ac9.jpg\" alt=\"\" /><br />\r\n<!--[endif]--></span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;\">成型的嫩豆腐，</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;\">回国后吃的人少了，我们改用</span><span lang=\"EN-US\">4</span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;\">两黄豆，</span><span lang=\"EN-US\">2.5mL</span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;\">内酯，</span><span lang=\"EN-US\">1200mL</span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;\">清水做成的内脂豆腐。方法一样。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\"><img src=\"/images/upload/Image/ac10.jpg\" alt=\"\" /><br />\r\n<!--[endif]--></span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;<img src=\"/images/upload/Image/ac11.jpg\" width=\"749\" height=\"561\" alt=\"\" />&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\"><img src=\"/images/upload/Image/ac12.jpg\" width=\"749\" height=\"561\" alt=\"\" /><br />\r\n<!--[endif]--></span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;\">上面一盆是用</span><span lang=\"EN-US\">4</span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;\">两黄豆做成的内脂豆腐</span><span lang=\"EN-US\">&nbsp;&nbsp;<img src=\"/images/upload/Image/ac13.jpg\" alt=\"\" /><!--[endif]--></span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;\">心得体会：做豆浆很简单，但学问可不少。开始做时内酯放多了，水和豆蛋白分离味苦涩，内酯放少了成豆腐花。我们经过失败总结到做成，享受了制作过程，收获了成功的喜悦。</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;\r\nmso-hansi-font-family:&quot;Times New Roman&quot;\">现在许多家庭都有豆浆机，自己磨豆浆喝，在此基础上做点内酯豆腐，变变花样吃，剩下的豆渣还可以和面粉</span><span lang=\"EN-US\">.</span><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;\r\nmso-hansi-font-family:&quot;Times New Roman&quot;\">鸡蛋、洋葱（胡萝卜等瓜菜）搅糊在一起煎成饼，营养丰富又好吃，剩下的豆渣还可以让其发酵当有机肥用</span><span lang=\"EN-US\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;mso-ascii-font-family:\r\n&quot;Times New Roman&quot;;mso-hansi-font-family:&quot;Times New Roman&quot;\">。</span><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span><span style=\"font-family:宋体;\r\nmso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:&quot;Times New Roman&quot;\">朋友们不妨也试试！</span></p>', '', '', '', '0', '1', '1361941789', '', '0', 'http://', '');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('223', '18', '卤水、石膏、内酯都说自己好，谁才是豆腐家族“超女” ————重点推荐！', '<p>&nbsp;<img src=\"/images/upload/Image/ss1.jpg\" width=\"478\" height=\"358\" alt=\"\" /></p>\r\n<p><img src=\"/images/upload/Image/ss2.jpg\" alt=\"\" /></p>\r\n<p><img src=\"/images/upload/Image/ss3.jpg\" alt=\"\" /></p>\r\n<p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span style=\"font-family: 宋体;\">《青岛晚报》昨日报道了北宅用醋点豆腐的独门绝活，其实生活中最常吃的还是卤水豆腐、石膏豆腐、内酯豆腐等。要是豆腐能说话，它们肯定都会说：我最棒，吃我吧<span lang=\"EN-US\">!</span>那谁才谁是豆腐家族的<span lang=\"EN-US\">&ldquo;</span>超女<span lang=\"EN-US\">&rdquo;</span>？就让它们<span lang=\"EN-US\">PK</span>一下吧！<span lang=\"EN-US\">&nbsp;<br />\r\n</span>卤水、石膏、内酯都说自己好谁才是豆腐家族<span lang=\"EN-US\">&ldquo;</span>超女<span lang=\"EN-US\">&rdquo;&nbsp;</span></span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span style=\"font-family: 宋体;\"><span lang=\"EN-US\"><br />\r\n</span><span style=\"color: rgb(255, 0, 0);\"><strong>第一轮：比营养<span lang=\"EN-US\">&nbsp;</span></strong></span><span lang=\"EN-US\"><br />\r\n</span>卤水豆腐各项指标领先<span lang=\"EN-US\">&nbsp;<br />\r\n</span>评委：市立医院营养科杨海平主任<span lang=\"EN-US\">&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;</span>杨海平主任告诉记者，以<span lang=\"EN-US\">100</span>克豆腐为标准，从三种豆腐的营养方面来讲：<span lang=\"EN-US\">&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;</span>卤水豆腐<span lang=\"EN-US\">&mdash;&mdash;</span>蛋白质<span lang=\"EN-US\">12.2</span>克，脂肪<span lang=\"EN-US\">4.8</span>克，镁<span lang=\"EN-US\">63</span>毫克，钙<span lang=\"EN-US\">138</span>毫克。<span lang=\"EN-US\">&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;</span>石膏豆腐<span lang=\"EN-US\">&mdash;&mdash;</span>蛋白质<span lang=\"EN-US\">6.2.</span>克，脂肪<span lang=\"EN-US\">2.5</span>克，镁<span lang=\"EN-US\">36</span>毫克，钙<span lang=\"EN-US\">116</span>毫克。<span lang=\"EN-US\">&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;</span>内酯豆腐<span lang=\"EN-US\">&mdash;&mdash;&nbsp;</span>（用葡萄糖酸内酯作凝固剂）：蛋白质<span lang=\"EN-US\">5.0</span>克，脂肪<span lang=\"EN-US\">1.9</span>克，镁<span lang=\"EN-US\">24</span>毫克，钙<span lang=\"EN-US\">17</span>毫克。<span lang=\"EN-US\">&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;</span>因此本轮<span style=\"color: rgb(255, 0, 0);\"><strong>卤水豆腐胜出</strong></span>。<span lang=\"EN-US\">&nbsp;</span></span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span style=\"font-family: 宋体;\"><span lang=\"EN-US\"><br />\r\n</span><span style=\"color: rgb(255, 0, 0);\"><strong>第二轮：比口感<span lang=\"EN-US\">&nbsp;</span></strong></span><span lang=\"EN-US\"><br />\r\n</span>石膏豆腐口味相对最好<span lang=\"EN-US\">&nbsp;<br />\r\n</span>评委：海慈医院营养科杨红主任<span lang=\"EN-US\">&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;</span>杨红主任则认为三种豆腐营养成分差不多，选择哪种豆腐可根据个人口味，不必过多关注具体的营养成分。从口感方面来说：<span lang=\"EN-US\">&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"color: rgb(255, 0, 0);\"><strong>卤水豆腐<span lang=\"EN-US\">&mdash;&mdash;</span>有点苦</strong></span>。一般以盐卤（氯化镁）点制，其特点是硬度较大、韧性较强、含水量较低，口感很<span lang=\"EN-US\">&ldquo;</span>粗<span lang=\"EN-US\">&rdquo;</span>，味微甜略苦，但蛋白质含量最高，宜煎、炸、做馅等。尽管北豆腐有点苦味，但其镁、钙的含量更高一些，能帮助降低血压和血管紧张度，预防心血管疾病的发生，还有强健骨骼和牙齿的作用。<span lang=\"EN-US\">&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\"color: rgb(255, 0, 0);\">石膏豆腐<span lang=\"EN-US\">&mdash;&mdash;</span>味甘甜</span></strong>。又称嫩豆腐、南豆腐。一般以石膏（硫酸钙）点制，其特点是质地细嫩，富有弹性，含水量大，味甘而鲜，蛋白质含量在<span lang=\"EN-US\">5%</span>以上。烹调宜拌、炒、烩、氽、烧及作羹等。<span lang=\"EN-US\">&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"color: rgb(255, 0, 0);\"><strong>内酯豆腐<span lang=\"EN-US\">&mdash;&mdash;</span>没有豆腐味</strong></span>。通常用葡萄糖酸内酯作为凝固剂，添加海藻糖和植物胶之类物质保水制成。有专家认为，虽然质地细腻，口感水嫩，但没有传统的豆腐有营养。这是因为，一来大豆含量少了，吃起来没有豆腐味；二来豆腐的钙和镁主要来自于石膏和卤水，而如今使用的葡萄糖酸内酯凝固剂既不含钙也不含镁，营养价值因而下降。<span lang=\"EN-US\">&nbsp;</span></span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span style=\"font-family: 宋体;\"><span lang=\"EN-US\"><br />\r\n</span>生活知识<span lang=\"EN-US\">&nbsp;<br />\r\n</span><span style=\"color: rgb(255, 0, 0);\"><strong>卤水有毒为啥做豆腐没事<span lang=\"EN-US\">&nbsp;</span></strong></span><span lang=\"EN-US\"><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;</span>一个杨白劳让大家牢牢记住卤水有毒。不过虽然卤水本身有毒<span lang=\"EN-US\">&nbsp;</span>（它是氯化镁氯化钠和硫酸镁的混合物），但是在点豆腐的过程中，其中的氯元素将豆浆中的钙离子置换出来，凝聚到一起再压成豆腐<span lang=\"EN-US\">&nbsp;</span>（这也是为什么豆腐比豆浆更补钙的原因），其卤水本身已经不再是卤水了，也就无毒了。当然化学变化有个量的问题，放多了会过剩，如果卤水加多了确实会因为反应不完全而使豆腐有毒。但是加多了卤水的豆腐就会变成黑色，你看到的白的豆腐肯定是卤水完全反应掉了的。因此，只要是正常做出来的豆腐，对人体是无害的。<span lang=\"EN-US\">&nbsp;</span></span></span><span style=\"font-family:宋体\"><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n</p>', '', '', '', '0', '1', '1361942186', '', '0', 'http://', '');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('224', '18', '如何区别卤水豆腐和石膏豆腐（1） ————重点推荐！', '<p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span style=\"font-family: 宋体;\">日前，永康市民胡女士在永康农贸市场买了一块豆腐，回家之后发现袋子里渗出很多水，而且刚一伸手拿，豆腐就掉下来一块，切的时候也碎得很厉害，和以前买的卤水豆腐有很大区别。胡女士的丈夫过来看了一下这块豆腐，又闻了闻说：<span lang=\"EN-US\">&ldquo;</span>这根本不是什么卤水豆腐，是石膏豆腐。<span lang=\"EN-US\">&rdquo;</span></span></span><span style=\"font-family:宋体\"><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span lang=\"EN-US\" style=\"font-family: 宋体;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family: 宋体;\">那么，卤水豆腐和石膏豆腐究竟有何区别，是否对人体有害？记者咨询了曾在永康市华丰菜场经营豆制品生意的应华梅。</span></span><span style=\"font-family:宋体\"><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p class=\"MsoNormal\"><strong><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-size: small;\"><span lang=\"EN-US\" style=\"font-family: 宋体;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family: 宋体;\">主要区别是口感</span></span></span></strong><span style=\"font-family:宋体\"><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span lang=\"EN-US\" style=\"font-family: 宋体;\">&nbsp;&nbsp;&nbsp;&nbsp;&ldquo;</span><span style=\"font-family: 宋体;\">卤水豆腐和石膏豆腐的主要区别是口感。<span lang=\"EN-US\">&rdquo;</span>应华梅说，<span style=\"color: rgb(255, 0, 0);\"><strong>石膏点的豆腐会把更多的水包在豆腐中，使豆腐含水多，这样的豆腐口感嫩，但是味道淡、不好吃</strong></span>。不过，<span style=\"color: rgb(255, 0, 0);\"><strong>水多就压秤</strong></span>，所以现在很多商家<span style=\"color: rgb(255, 0, 0);\"><strong>为了多赚钱都会卖石膏豆腐</strong></span>。</span></span><span style=\"font-family:宋体\"><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span lang=\"EN-US\" style=\"font-family: 宋体;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family: 宋体;\">据了解，卤水的主要成分是氯化钠、氯化镁，还有一些硫酸盐。石膏的主要成份是硫酸钙。<span style=\"color: rgb(255, 0, 0);\"><strong>卤水和石膏的主要成分都可以起到凝结豆腐的作用。石膏在使用的时候凝结度高，因此，同样的豆腐原料，可以制作出更多的豆腐，商家为追求产量、多赚钱，难免造假</strong></span>。</span></span><span style=\"font-family:宋体\"><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span lang=\"EN-US\" style=\"font-family: 宋体;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family: 宋体;\">此外，<span style=\"color: rgb(255, 0, 0);\"><strong>石膏豆腐的价格比卤水豆腐要低一些</strong></span>。</span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family:宋体\"><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span lang=\"EN-US\" style=\"font-family: 宋体;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family: 宋体;\">辨认技巧</span></span><span style=\"font-family:宋体\"><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span lang=\"EN-US\" style=\"font-family: 宋体;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-family: 宋体;\">石膏豆腐质地比较软嫩、细腻，但无豆香气。卤水豆腐要坚硬一些，切面也不如石膏豆腐细滑，而且有许多小的蜂窝状的眼。</span></span></strong></span><span style=\"font-family:宋体\"><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span lang=\"EN-US\" style=\"font-family: 宋体;\">&nbsp;&nbsp;&nbsp;</span><span style=\"color: rgb(255, 0, 0);\"><strong><span lang=\"EN-US\" style=\"font-family: 宋体;\">&nbsp;</span><span style=\"font-family: 宋体;\">卤水豆腐比较硬、有豆香味、含水量少，色泽白中略偏黄，质地比较粗老，俗称<span lang=\"EN-US\">&ldquo;</span>老豆腐<span lang=\"EN-US\">&rdquo;</span></span></strong></span><span style=\"font-family: 宋体;\">，主要用于煎、炸、酿以及制馅等。</span></span><span style=\"font-family:宋体\"><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span lang=\"EN-US\" style=\"font-family: 宋体;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"color: rgb(255, 0, 0);\"><strong><span style=\"font-family: 宋体;\">石膏豆腐细嫩光滑、含水量多、色泽洁白，俗称<span lang=\"EN-US\">&ldquo;</span>嫩豆腐<span lang=\"EN-US\">&rdquo;</span></span></strong></span><span style=\"font-family: 宋体;\">，主要用于拌、烩、烧，制作汤、羹等菜肴。</span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family:宋体\"><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span lang=\"EN-US\" style=\"font-family: 宋体;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family: 宋体;\">另外，应华梅强调说，虽然两种豆腐的<span style=\"color: rgb(255, 0, 0);\"><strong>口感差别较大，但营养价值相差无几</strong></span>。消费者需要注意的是，<u><span style=\"color: rgb(255, 0, 0);\"><strong>千万不要购买用大豆分离蛋白制成的人工合成豆腐，这种豆腐对人体危害极大</strong></span></u>。</span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span lang=\"EN-US\" style=\"font-family: 宋体;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family: 宋体;\">消费者选购豆腐时要一看、二摸、三闻，以防购买到用劣质大豆制作的豆腐。</span></span><span style=\"font-family:宋体\"><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span lang=\"EN-US\" style=\"font-family: 宋体;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><u><span style=\"color: rgb(255, 0, 0);\"><strong><span style=\"font-family: 宋体;\">优质豆腐呈均匀的乳白色或淡黄色，稍有光泽，颜色发黑、发暗的豆腐尽量不要购买</span></strong></span></u><span style=\"font-family: 宋体;\">；用手按压豆腐表面可以感受到一定的弹性，要软硬适度；常温下直接嗅其气味，豆腐是以大豆为原料制成的，有大豆的香味，如果<u><span style=\"color: rgb(255, 0, 0);\"><strong>没有自然豆香味的豆腐，尽量不要购买</strong></span></u>。</span></span></p>\r\n</p>\r\n<p>&nbsp;</p>', '', '', '', '0', '1', '1361942464', '', '0', 'http://', '');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('225', '18', '如何区别卤水豆腐和石膏豆腐（2） ————重点推荐！', '<p><span style=\"font-size: small;\"><span style=\"font-family: 宋体;\">如何远离问题豆制品</span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span style=\"font-family: 宋体;\"><span lang=\"EN-US\"> <br />\r\n</span>豆腐是白是黑，关键是执法部门的执法力度问题，不过，在政府管理还没有完全跟进的时候，消费者学会自救是有必要的。</span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span style=\"font-family: 宋体;\"><span lang=\"EN-US\"><br />\r\n<br />\r\n</span>首先，<strong><span style=\"color: rgb(255, 0, 0);\"><u>最好不要去那些信誉、卫生没有保障的路边小摊、小餐馆</u></span></strong>（不包括那些信誉好的小店）进食，他们很可能使用黑作坊生产的豆制品。这些<span style=\"color: rgb(255, 0, 0);\"><u><strong>豆制品中即使没有医用废弃石膏、<span lang=\"EN-US\">&ldquo;</span>吊白块<span lang=\"EN-US\">&rdquo;</span>、工业盐，卫生状况也值得担忧</strong></u></span>。<span lang=\"EN-US\"><br />\r\n<br />\r\n</span>其次，<span style=\"color: rgb(255, 0, 0);\"><u><strong>警惕又薄又黄的豆腐皮，其中可能有<span lang=\"EN-US\">&ldquo;</span>吊白块<span lang=\"EN-US\">&rdquo;</span>、工业盐、工业色素等</strong></u></span>。购买豆制品前仔细闻一下，<span style=\"color: rgb(255, 0, 0);\"><u><strong>如果闻到刺鼻的酸味，则必定有问题；不要相信那些有酱色的豆腐干是用酱油泡出来的谎言，那是多种化学原料炮制的结果，其中少不了工业盐</strong></u></span>。</span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span style=\"font-family: 宋体;\"><span lang=\"EN-US\"><br />\r\n<br />\r\n</span>怎样辨别真假豆腐<span lang=\"EN-US\"><br />\r\n<br />\r\n&nbsp;</span></span><span style=\"color: rgb(255, 0, 0);\"><u><strong><span style=\"font-family: 宋体;\"><span lang=\"EN-US\"> &nbsp; 1</span>．看：取一块豆腐在散射光线下直接观察。传统豆腐呈均匀的乳白色或淡黄色，稍有光泽。而人工合成豆腐因为加入了白色素，比起传统豆腐要白得多，而且比较无光泽<span lang=\"EN-US\">;</span>也可能内有水纹、气泡、细微颗粒等。<span lang=\"EN-US\"><br />\r\n<br />\r\n</span>　　<span lang=\"EN-US\">2</span>．摸：传统豆腐用手按压可以感受到一定的弹性，而且软硬适度。人工合成的豆腐切面处会比较粗糙，质地不细腻，弹性较差，且没有白色豆腐液体流出。<span lang=\"EN-US\"><br />\r\n<br />\r\n</span>　　<span lang=\"EN-US\">3</span>．闻：在常温下直接嗅闻其气味。传统豆腐是以大豆为原料制成，有大豆的香味。而人工合成豆腐是用大豆分离蛋白制成，气味很淡，甚至有化学药剂的味道。</span></strong></u></span></span><span style=\"font-family:宋体\"><span lang=\"EN-US\"><o:p></o:p></span></span></p>', '', '', '', '0', '1', '1361942582', '', '0', 'http://', '');
