INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('243', '18', '教你如何买牛肉 ————重点推荐！', '<p><span style=\"font-size: small;\"><span style=\"font-family: 宋体;\">对于市场上销售的鲜牛肉和冻牛肉，消费者该如何选购呢？工商人员建议市民可从<span style=\"color: rgb(255, 0, 0);\"><strong>色泽、气味、黏度、弹性、肉汤等</strong></span>方面进行鉴别。</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: 宋体;\">具体来说如下：</span></span></p>\r\n<p><span style=\"font-size: small;\"><span lang=\"EN-US\"><img src=\"/images/upload/Image/ss2(1).jpg\" width=\"772\" height=\"529\" alt=\"\" /><br />\r\n</span></span><span style=\"font-size: small;\"><strong><span style=\"color: rgb(255, 0, 0);\"><span lang=\"EN-US\"><br />\r\n</span><span style=\"font-family: 宋体;\">色泽鉴别</span><span lang=\"EN-US\"><br />\r\n1</span><span style=\"font-family: 宋体;\">、新鲜肉&mdash;&mdash;肌肉呈均匀的红色，具有光泽，脂肪洁白色或呈乳黄色。</span><span lang=\"EN-US\"><br />\r\n2</span><span style=\"font-family: 宋体;\">、次鲜肉&mdash;&mdash;肌肉色泽稍转暗，切面尚有光泽，但脂肪无光泽。</span><span lang=\"EN-US\"><br />\r\n3</span><span style=\"font-family: 宋体;\">、变质肉&mdash;&mdash;肌肉色泽呈暗红，无光泽，脂肪发暗直至呈绿色。</span></span></strong></span></p>\r\n<p><span style=\"font-size: small;\"><span lang=\"EN-US\"><br />\r\n</span><span style=\"color: rgb(255, 0, 0);\"><strong><span style=\"font-family: 宋体;\">气味鉴别</span><span lang=\"EN-US\"><br />\r\n1</span><span style=\"font-family: 宋体;\">、新鲜肉&mdash;&mdash;具有鲜牛肉的特有正常气味。</span><span lang=\"EN-US\"><br />\r\n2</span><span style=\"font-family: 宋体;\">、次鲜肉&mdash;&mdash;稍有氨味或酸味。</span><span lang=\"EN-US\"><br />\r\n3</span><span style=\"font-family: 宋体;\">、变质肉&mdash;&mdash;有腐臭味。</span></strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span lang=\"EN-US\"><br />\r\n</span><span style=\"color: rgb(255, 0, 0);\"><strong><span style=\"font-family: 宋体;\">黏度鉴别</span><span lang=\"EN-US\"><br />\r\n1</span><span style=\"font-family: 宋体;\">、新鲜肉&mdash;&mdash;表面微干或有风干膜，触摸时不粘手。</span><span lang=\"EN-US\"><br />\r\n2</span><span style=\"font-family: 宋体;\">、次鲜肉&mdash;&mdash;表面干燥或粘手，新的切面湿润。</span><span lang=\"EN-US\"><br />\r\n3</span><span style=\"font-family: 宋体;\">、变质肉&mdash;&mdash;表面极度干燥或发粘，新切面也粘手。</span></strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span lang=\"EN-US\"><br />\r\n</span><span style=\"color: rgb(255, 0, 0);\"><strong><span style=\"font-family: 宋体;\">弹性鉴别</span><span lang=\"EN-US\"><br />\r\n1</span><span style=\"font-family: 宋体;\">、新鲜肉&mdash;&mdash;指压后的凹陷能立即恢复。</span><span lang=\"EN-US\"><br />\r\n2</span><span style=\"font-family: 宋体;\">、次鲜肉&mdash;&mdash;指压后的凹陷恢复较慢，并且不能完全恢复。</span><span lang=\"EN-US\"><br />\r\n3</span><span style=\"font-family: 宋体;\">、变质肉&mdash;&mdash;指压后的凹陷不能恢复，并且留有明显的痕迹。</span></strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span lang=\"EN-US\"><br />\r\n</span><span style=\"color: rgb(255, 0, 0);\"><strong><span style=\"font-family: 宋体;\">肉汤鉴别</span><span lang=\"EN-US\"><br />\r\n1</span><span style=\"font-family: 宋体;\">、良质冻牛肉</span><span lang=\"EN-US\">(</span><span style=\"font-family: 宋体;\">解冻肉</span><span lang=\"EN-US\">)</span><span style=\"font-family: 宋体;\">&mdash;&mdash;肉汤汁透明澄清，脂肪团聚浮于表面，具有一定的香味。</span><span lang=\"EN-US\"><br />\r\n2</span><span style=\"font-family: 宋体;\">、次质冻牛肉</span><span lang=\"EN-US\">(</span><span style=\"font-family: 宋体;\">解冻后</span><span lang=\"EN-US\">)</span><span style=\"font-family: 宋体;\">&mdash;&mdash;汤汁稍有混浊，脂肪呈小滴浮于表面，香味鲜味较差。</span><span lang=\"EN-US\"><br />\r\n3</span><span style=\"font-family: 宋体;\">、变质冻牛肉</span><span lang=\"EN-US\">(</span><span style=\"font-family: 宋体;\">解冻后</span><span lang=\"EN-US\">)</span><span style=\"font-family: 宋体;\">&mdash;&mdash;肉汤混浊，有黄色或白色絮状物，浮于表面的脂肪极少，有异味。</span></strong></span></span></p>\r\n<p>&nbsp;</p>\r\n<p>附：</p>\r\n<p><img src=\"/images/upload/Image/ss1(1).jpg\" alt=\"\" /></p>', '', '', '', '0', '1', '1362375024', '', '0', 'http://', '');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('244', '17', '3月初考虑大家喜好，补买的5种种子', '<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">1、这两款香瓜，是被农民和种子店极力推荐的。一个一斤，合适的话，就放进蔬菜套餐里面给大家自由选择，貌似说很甜很脆。</span></strong></span></p>\r\n<p><img src=\"/images/upload/Image/da1(2).jpg\" alt=\"\" /></p>\r\n<p><img src=\"/images/upload/Image/da3(1).jpg\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">2、这是镇江这边常吃的扁豆，这是会员&ldquo;苹果&rdquo;喊出来想吃，不过不喜欢吃扁豆的人也有，喜欢吃扁豆，但是不喜欢吃红色扁豆，却喜欢吃白色的也有。</span></strong></span></p>\r\n<p><img src=\"/images/upload/Image/da2(1).jpg\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">3、增加了西瓜，也是小个子的。</span></strong></span></p>\r\n<p><img src=\"/images/upload/Image/da4(1).jpg\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">4、增加了胡萝卜，因为喊了要吃的人太多了！</span></strong></span></p>\r\n<p><img src=\"/images/upload/Image/da6(1).jpg\" width=\"750\" height=\"550\" alt=\"\" /></p>', '', '', '', '0', '1', '1362715592', '', '0', 'http://', '');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('245', '17', '2012年4月考察上党仙居鸡生态农场——刘生权一家情景', '<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">4月份去的时候，已经是一片绿色。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/df1.jpg\" alt=\"\" /><br />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/df2.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">其实鸡比这个跑在外面的多的多，汽车开过，四处乱跑。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/df3.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/df4.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/df20.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">这是仙居鸡小老板、湖北人刘生权。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/df6.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/df7.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/df8.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">外面还种了不少蔬菜，自己家吃，多余的给鸡吃。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/df12.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">样子虽然不怎么样，这可以春韭。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/df13(1).jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/df14.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">仙居鸡其实种榉树和红叶石楠等树苗是比较专业的，算是在市场上活下来的农户。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/df15.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">玉米等均在此处粉碎。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/df16.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/df17.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">地处一个丘陵地带，周边环境还是不错的。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/df18.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">这是刘生权的老爹。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/df19.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">身体还不错，人也不错。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/df21.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">这是给鸡用的中药，小鸡一出来要打一次青霉素，之后若是生病，要么淘汰，要么喂中药，这个正清散只是其中的中药之一。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/df22.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span