INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('251', '17', '2013年3月13日0农药有机肥蔬菜记录', '<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">春韭出来了，去年韭菜种子出了问题，只有这点苟延残喘，这长出来还要割掉，貌似这第一波韭菜，孩子吃了容易起疙瘩、浑身痒，每次可能只能供应2户，哈哈哈，等爆出来，挨个排队吧。大量的上市，需要等到7月了。&nbsp;</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds1(1).jpg\" alt=\"\" /></span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds2.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">紫叶罗莎生菜，爱紫色系列蔬菜的人，这几个月是爽了。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds3.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds4.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">雷吉纳紫生菜，已经快吃完了。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds5.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">这是新品种美国紫生菜，可以吃咯。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds7.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds8.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">这是很好吃的奶油生菜，也差不多了。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds9.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds10.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds11.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds19.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">这是结球生菜。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds12.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">这是面包菊苣。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds13.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds14(1).jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds18.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">这是芥蓝，好东西。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds15.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds16.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds17(1).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">冰心紫荷，名字好听的紫生菜，长大了就可以吃了。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds20.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">在新育苗的小西红柿。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds21.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">老鼠单单把紫枣这个品种的种子全啃光了，要补种了，奶奶的。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds22.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds23.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">苦苣是大家爱吃的，继续补种了。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds28(2).jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">紫包菜可能全军覆没，全部在抽薹。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds29.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">看到包菜内部在顶的那种感觉了么，包菜估计要抽薹三分之一。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds30.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds31.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ds32.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">荠菜都开花了~。</span></strong></span></p>\r\n<p><span style=\"co