INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('259', '17', '2013年3月再次考察正东菌菇（1）', '<p><span style=\"color: rgb(0, 0, 0);\"><strong><span style=\"font-size: small;\">因为下雨，只是简单的拍摄了正东生态农业发展中心&mdash;&mdash;简称正东菌菇的周边环境。</span></strong></span></p>\r\n<p><span style=\"color: rgb(0, 0, 0);\"><strong><span style=\"font-size: small;\">真可谓，依山傍水。</span></strong></span></p>\r\n<p><span style=\"color: rgb(0, 0, 0);\"><strong><span style=\"font-size: small;\">公司董事长貌似就住在这里面，真是爽歪歪了。</span></strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"color: rgb(0, 0, 0);\"><strong><span style=\"font-size: small;\">主要特点有2：</span></strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"color: rgb(255, 0, 0);\"><strong><span style=\"font-size: small;\">（1）回避了大多数菌菇厂使用棉籽壳、大豆粉、木屑的安全风险，而使用了玉米芯和糠。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 0, 0);\"><strong><span style=\"font-size: small;\">棉籽壳和大豆粉基本上都是转基因产品，而木屑中含有一定的工业胶等物质，这是对人体有害的。</span></strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"color: rgb(255, 0, 0);\"><strong><span style=\"font-size: small;\">（2）使用了山泉水，水质高，有水质检测报告，而不使用漂白粉含量较多的自来水。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(0, 0, 0);\"><strong><span style=\"font-size: small;\">综合以上介绍，请期待下个月的详细报道！</span></strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><img src=\"/images/upload/Image/ww5.jpg\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p><img src=\"/images/upload/Image/ww2.jpg\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p><img src=\"/images/upload/Image/ww1(1).jpg\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p><img src=\"/images/upload/Image/ww3.jpg\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p><img src=\"/images/upload/Image/ww4.jpg\" alt=\"\" /></p>', '', '', '', '0', '1', '1363407946', '', '0', 'http://', '');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('261', '17', '2013年3月20日0农药有机肥蔬菜记录', '<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">&nbsp;粘虫黄板挂起来了。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss1(2).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss2(2).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">利用虫子的趋光性，在虫子飞翔的高度附近（60cm上下）挂上粘虫黄板，可以有效除一部分虫。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss3(1).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss4.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">贝妮番茄的苗。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss6.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">丝瓜即将移栽。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss7(1).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">这是雨中风铃爱吃的四月白小青菜。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss8.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">这是普通的小青菜。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss9.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">大妈去割最后的药芹。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss10.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">这是油菜菜心，过阵子就可以吃了。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss11.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">这是大家的大爱&mdash;&mdash;翡翠黄瓜，稍微密集了点。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss12.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss13.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss14.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss15.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">这是另外一种牛心包菜，深绿色，已经包心了。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss16.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">蚕豆开花了，好东西。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss17.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss18.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">我们冬季吃的小青菜都开花了，四处一片花海。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss19.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">架豆苗大了。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss20.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">西葫芦发芽率不高，继续补种。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss21.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss22.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">紫包菜大部分都抽薹，可能或许能吃到个几个。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss23.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">苦苣是大家都爱吃的，育了不少。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss24.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">极品罗莎生菜，可以吃了，特别紫的一种高档生菜。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss25.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss34.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">这是冰心紫荷，还要等等。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss27.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">面包菊苣还要等包心。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss28.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">这是众多里面唯一一株变异品种，当然，不能吃了。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss33(1).jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">芥蓝，好东西，可惜的是，今天一下子被吃了一半。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss29.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss30.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss31.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">紫罗兰青菜抽薹准备开花了。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss32.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">樱桃萝卜稍微恢复一下，周六可以点，但是大家别点的太多，估计这次也就是10斤左右的量。后面补种了不少。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss35.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">伏玉包菜全军覆没，我还是比较怀疑，因为捏起来比较硬，想想还是切开看了几个，都是这样，果断停吃。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss36.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss37.jpg\" width=\"750\" height=\"550\" alt=\"\" /></span></strong></span></p>', '', '', '', '0', '1', '1363730337', '', '0', 'http://', '');
