INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('264', '18', '中国的食品等级（图解）', '<p>&nbsp;<img src=\"/images/upload/Image/84f4220fjw1e2ya0z5bgxj.jpg\" width=\"559\" height=\"690\" alt=\"\" /></p>', '', '', '', '0', '1', '1363886545', '', '0', 'http://', '');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('265', '18', '野鸽蛋和家鸽蛋、鹌鹑蛋的区别', '<p><strong><span style=\"font-size: small;\"><span style=\"background-color: rgb(255, 255, 255);\">&nbsp;<span style=\"color: rgb(83, 54, 76); font-family: Arial, Helvetica, sans-serif; line-height: 19px;\">野鸽蛋和家鸽蛋、鹌鹑蛋的区别：</span></span></span></strong></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(83, 54, 76); font-family: Arial, Helvetica, sans-serif; line-height: 19px;\"> </span></span><strong><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-family: Arial, Helvetica, sans-serif; line-height: 19px;\">1、家鸽蛋为纯白色，表面光滑，呈透明状，野鸽蛋为灰白色，表面有斑点，较粗糙；</span></span></span></strong></span></p>\r\n<p><strong><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: small;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"font-family: Arial, Helvetica, sans-serif; line-height: 19px;\">2、家鸽蛋个头要比野鸽蛋大；</span></span></span></span></strong></p>\r\n<p><strong><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: small;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"font-family: Arial, Helvetica, sans-serif; line-height: 19px;\">3、野鸽蛋比鹌鹑蛋个头大；野鸽蛋比于鹌鹑蛋颜色浅，鹌鹑蛋为黑白色；</span></span></span></span></strong></p>\r\n<p><strong><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: small;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"font-family: Arial, Helvetica, sans-serif; line-height: 19px;\">4、野鸽蛋的斑点比较小，呈粒状，鹌鹑蛋的斑点呈块状</span></span></span></span></strong></p>\r\n<p>&nbsp;</p>\r\n<p><img src=\"/images/upload/Image/6a71de04gw1dk9hb3s3p4j.jpg\" width=\"500\" height=\"375\" alt=\"\" /></p>', '', '', '', '0', '1', '1363900523', '', '0', 'http://', '');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('266', '17', '2013年3月23日0农药有机肥蔬菜记录', '<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">芥蓝登场2次就被吃光光了，要翻倍补种了。&nbsp;</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/qq1(1).jpg\" alt=\"\" /></span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">冰心紫荷，等等就好吃了。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/qq2.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">樱桃萝卜，再来2周就好吃了，这次种了很多，好吃的要持续的吃。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/qq3.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">绿翡翠生菜，半包球，很好吃！</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/qq4.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">紫罗兰青菜，发芽了，绕过抽薹季节，下面可以持续的吃了。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/qq5.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">这是芝麻菜，真和芝麻一样，哈哈~。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/qq6.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">苹果梨的婆婆在去年10月到农场来玩的时候，说希望有点茴香，这不，还没发芽，但是有了，可以包饺子，北方人爱吃。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/qq7.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">土豆和豌豆苗的套种，土豆都快看不见了，过阵子，直接豌豆苗作为绿肥，翻到地里就行了。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/qq8.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">5月吃土豆！</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/qq9.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/qq10.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">奶油生菜，适合生吃，非常鲜嫩，长的这个样子，就算对了。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/qq11.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">土壤有机质改善的不错，看着土质，以前可是黄土。这也是将豌豆苗作为绿肥翻入地下的一个例子。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/qq12.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/qq13.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">这是绿翡翠黄瓜。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/qq14.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">牛心包菜从现在开始停1-2周，等这个长大一些，大家开吃吧，看看这种品种的牛心包菜好吃还是刚结束的那种嫩绿色的好吃。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/qq15.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/qq16.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">这是红色的一种油麦菜，按道理是比一般的那种油麦菜好吃。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">油麦菜一般是原种，是一种非常好的抗癌蔬菜，可能有人觉得有些怪味，但是还是建议大家多吃。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/qq17.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">四季豆这么大了，略微有点病害或者是高温烧伤的感觉。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/qq18.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">冬季种在外面的娃娃菜，完全抽薹开花了。这边马上就会准备种西瓜和3种香瓜。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/qq19.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">堆肥翻堆了一次，因为过于庞大，用挖机翻了一下。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/qq20.jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">看到没，左边翻到了右边，这样氧气进入菌堆，已经开始发热了。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/qq21.jpg\" alt=\"\" /></span></strong></span></p>', '', '', '', '0', '1', '1364075627', '', '0', 'http://', '');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('267', '17', '2013年3月27日0农药有机肥蔬菜记录', '<p><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: small;\"><strong>新品种的牛心包菜，卖相还不错。，准备下周开吃。</strong></span></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: small;\"><strong><img src=\"/images/upload/Image/ww1(2).jpg\" width=\"750\" height=\"550\" alt=\"\" /></strong></span></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: small;\"><strong><img src=\"/images/upload/Image/ww2(1).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</strong></span></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: small;\"><strong><img src=\"/images/upload/Image/ww15.jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</strong></span></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: small;\"><strong><br />\r\n</strong></span></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: small;\"><strong>新的樱桃萝卜叶子长出来了。</strong></span></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: small;\"><strong><img src=\"/images/upload/Image/ww3(1).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</strong></span></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: small;\"><strong><br type=\"_moz\" />\r\n</strong></span></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: small;\"><strong>清除了开花的豌豆苗，清爽了不少，土豆茎叶都长出来了。</strong></span></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: small;\"><strong><img src=\"/images/upload/Image/ww4(1).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</strong></span></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><span style=\