INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('270', '17', '2013年4月3日0农药有机肥蔬菜记录', '<p><span style=\"color: rgb(0, 0, 0);\"><strong><span style=\"font-size: small;\">大棚内温度很高，2秒镜头就能模糊，换了傻瓜机拍的，效果还行~。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">&nbsp;</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">这是球生菜，水分足、较甜，继续育苗，准备移栽了。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss1(3).jpg\" width=\"750\" height=\"550\" alt=\"\" /></span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">樱桃萝卜的叶子大了，估计还要2周。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss2(3).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">避开了抽薹季节，紫罗兰青菜，过阵子可以移栽了~</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss3(2).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">芝麻菜长大咯~</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss4(1).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">土豆啊土豆，快快长~</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss6(1).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">鸡毛菜都长成小青菜了~</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss7(2).jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">去年夏天没见大家这么爱吃苦苣啊，怎么一下子这么多人吃~，终于长的有点大了~，估计这波吃到三分之一，就得立刻育苗。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss8(1).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss9(2).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">球生菜终于结球了。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss10(1).jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">油菜菜心看到抽薹的迹象了。</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss11(1).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss12(1).jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">看，莴苣，越长越高，也要抽薹开花的迹象了~</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss13(1).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">空心菜~，啥时候才能吃啊~</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss14(1).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">我最讨厌的丝瓜，长的还不错~</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss15(1).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">水果黄瓜~</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss16(1).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss17(1).jpg\" width=\"750\" height=\"550\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">玉米苗几天不见就这么大了~</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss18(2).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">绿翡翠黄瓜~</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss19(2).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">这是第二波的鸡毛菜，第一波要改为小青菜了~</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss21(1).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">红油麦菜，长大咯~</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss22(1).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss23(1).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">这是芥蓝的苗，这个还要继续再种一批~</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><img src=\"/images/upload/Image/ss24(1).jpg\" alt=\"\" /><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\"><br type=\"_moz\" />\r\n</span></strong></span></p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">