INSERT INTO `ecs_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `biaozhun` ) VALUES  ('135', '11', '000135', '“李家”红菜苔', '+strong', '70', '21', '', '10', '0.5', '8.00', '8.00', '0.00', '0', '0', '1', '', '蔬菜春化阶段出现的特定的菜，营养高、清脆爽口！', '<p>&nbsp; &nbsp;<span style=\"color: rgb(255, 102, 0);\"><strong><span lang=\"EN-US\" style=\"font-family: 宋体;\">1</span><span style=\"font-family: 宋体;\">、简介：</span></strong></span><strong style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, 宋体, Verdana, Arial; font-size: small; line-height: 18px;\"><span style=\"border: 0px; font-weight: inherit; font-style: inherit; vertical-align: baseline; list-style: none; font-size: 12px; font-family: 宋体; color: rgb(255, 102, 0);\">（下图为实物图）</span></strong></p>\r\n<p><img src=\"/images/upload/Image/da1(1).jpg\" alt=\"\" /></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\">红菜苔，又名&ldquo;芸菜苔&rdquo;。色紫红、花金黄，是武汉地区的特产。据史籍记载，红菜苔在唐代是著名的蔬菜，历来是湖北地方向皇帝进贡的土特产，曾被封为&ldquo;金殿玉菜&rdquo;，与武昌鱼齐名。</span><span style=\"font-family: 宋体;\"><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><span style=\"color: rgb(255, 102, 0);\"><span lang=\"EN-US\">&nbsp;</span><strong><span lang=\"EN-US\" style=\"font-family: 宋体;\">2</span><span style=\"font-family: 宋体;\">、红菜苔的地理分布：</span></strong></span></span><span lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p><span style=\"color: rgb(0, 0, 0);\"> </span></p>\r\n<p>&nbsp;</p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span style=\"font-family: 宋体;\">红菜苔，久经传播，产地甚广，今已传入湖北各地及江西、四川、湖南等地。那么究竟以何地产者为佳呢？</span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span style=\"font-family: 宋体;\">据史载：红菜苔，俗名油菜苔，与城东宝通寺</span><span lang=\"EN-US\">(</span><span style=\"font-family: 宋体;\">在武汉</span><span lang=\"EN-US\">)</span><span style=\"font-family: 宋体;\">相近者，其味尤佳。亦有传曰：以宝通钟声所及处产者为佳。</span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span style=\"font-family: 宋体;\">历史曾有一种奇异地说法：以宝通塔影所及处产者最佳，现行小学六年制语文实验课本第四册中《红菜苔》篇亦有类似记载。</span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: small;\"><span style=\"font-family: 宋体;\">现在红菜苔则多以洪山附近产者为佳，霜降之后更为鲜嫩爽口。有人以色评曰；<span style=\"color: rgb(255, 102, 0);\"><strong>深者为佳，淡者次之</strong></span>。</span></span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp;</span><strong><span lang=\"EN-US\" style=\"font-family: 宋体;\">3</span><span style=\"font-family: 宋体;\">、红菜苔的历史：</span></strong></span></p>\r\n<p><span style=\"font-size: small;\"><span lang=\"EN-US\" style=\"font-family: 宋体;\"><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\">红菜苔</span></span><span style=\"font-family: 宋体;\">历史悠久，据《生物史》载：<span lang=\"EN-US\">&ldquo;<span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">武昌</span></span></span>特产红菜苔，在唐代时已是名菜。<span lang=\"EN-US\">&rdquo;</span>足见<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">红菜苔</span></span></span>早于千余年前已负盛名。<span lang=\"EN-US\">1907</span>，徐毓华所著《湖北乡土地理》载：<span lang=\"EN-US\">&ldquo;<span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">马鞍</span></span></span>之煤、<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">宝通寺</span></span></span>之菜，<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">黄鹄</span></span></span>矶头之鲤，均占优胜之土物也。<span lang=\"EN-US\">&rdquo;<span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">红菜苔</span></span></span>虽为一般蔬菜，然可与<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">马鞍</span></span></span>之煤、<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">黄鹄</span></span></span>矶头之鲤媲美，其身价非同一般矣。</span></span></p>\r\n<p><span style=\"font-family: 宋体;\"><span lang=\"EN-US\"><o:p></o:p></span></span><span style=\"font-size: small;\"><span lang=\"EN-US\" style=\"font-family: 宋体;\"><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\">武汉</span></span><span style=\"font-family: 宋体;\">的名菜菜苔。<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">红菜苔</span></span></span>，据说原仅产于<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">武昌</span></span><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">洪山</span></span></span>一带。其营养价值较高，脆嫩爽口，别具风味。菜苔别名<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">菜心</span></span></span>，是<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">十字花科</span></span></span>薹属种、<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">白菜</span></span></span>亚种中以花苔为产品的变种，<span lang=\"EN-US\">1</span>年生<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">草本植物</span></span></span>，目前已是<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">中国</span></span></span>出口港澳及<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">东南亚</span></span></span>的重要蔬菜之一。<span style=\"color: rgb(255, 102, 0);\"><strong>它营养丰富，含有<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"text-decoration: none;\"><span lang=\"EN-US\">钙</span></span></span>、<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"text-decoration: none;\"><span lang=\"EN-US\">磷</span></span></span>、<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"text-decoration: none;\"><span lang=\"EN-US\">铁</span></span></span>、<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"text-decoration: none;\"><span lang=\"EN-US\">胡萝卜素</span></span></span>、<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"text-decoration: none;\"><span lang=\"EN-US\">抗坏血酸</span></span></span>等成分，丙种维生素比<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"text-decoration: none;\"><span lang=\"EN-US\">大白菜</span></span></span>、<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"text-decoration: none;\"><span lang=\"EN-US\">小白菜</span></span></span>都高。且色泽艳丽，质地脆嫩，为佐餐之佳品。<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"text-decoration: none;\"><span lang=\"EN-US\">洪山</span></span></span>菜苔，尤以洪山<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"text-decoration: none;\"><span lang=\"EN-US\">宝通寺</span></span></span>之<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"text-decoration: none;\"><span lang=\"EN-US\">卓刀泉</span></span></span>九岭十八凹一带出产的品质最佳。优质<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"text-decoration: none;\"><span lang=\"EN-US\">红菜苔</span></span></span>只产在<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"text-decoration: none;\"><span lang=\"EN-US\">洪山</span></span></span>，若迁地移植，不仅颜色不同，口味也有差异。</strong></span>民国初年，<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">黎元洪</span></span></span>离开湖北，到<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">北京</span></span></span>当大总统时，每临冬天，必派专差到<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">洪山</span></span></span>来运<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">红菜苔</span></span></span>。由于长途大批运输，鲜菜运到<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">北京</span></span></span>后，时间一久，菜苔失去原有的色泽和鲜味，较之产地新鲜嫩菜苔当然逊色不少，常使食者感到美中不足。于是有人出谋把<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">洪山</span></span></span>的泥土装上几火车皮运往<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">北京</span></span></span>试种，结果，菜苔虽长出来了，但色不红、味不鲜。试种失败，更感到<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">洪山</span></span></span>菜苔之可贵，以后不得不沿用老办法，用火车成批运转菜苔到<span lang=\"EN-US\"><span lang=\"EN-US\" style=\"color: black; text-decoration: none;\"><span lang=\"EN-US\">北京</span></span></span>。</span></span><span style=\"font-family: 宋体;\"><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-size: small;\">4、上会佰亩杉农场简介：&nbsp;</span></strong></span></p>\r\n<p><img src=\"/images/upload/Image/c1.jpg\" alt=\"\" /></p>\r\n<p><img src=\"/images/upload/Image/c2.jpg\" width=\"646\" height=\"604\" alt=\"\" /></p>\r\n<p><img src=\"/images/upload/Image/c3.jpg\" alt=\"\" /></p>\r\n<p><img src=\"/images/upload/Image/c4.jpg\" alt=\"\" /></p>\r\n<p><img src=\"/images/upload/Image/c5.jpg\" width=\"772\" height=\"514\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"color: rgb(255, 102, 0);\"><strong><span style=\"font-family: 宋体;\">5、蔬菜生产农户李建峰（右）给大家的一句话：</span></strong></span><span lang=\"EN-US\"><o:p></o:p></span><span style=\"color: rgb(255, 102,